css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://play.google.com/music") {
/*search bar*/
#gbx1, #gbx2
{background: none repeat scroll 0 0 #111!important;}
/*search*/
.gbqfqw {

    background: none repeat scroll 0 0 #050505!important;
    border-color: #222 #222 #222!important;
    border-right: 1px solid #222!important;}
input, textarea
{color:white!important;
}    

/*nav bar*/
.music-banner,.nav-bar,#breadcrumbs,.gb_qb {color:white!important;
background-color:#111!important;}
.gb_qb{height:61px!important;}

/*nav*/
#nav,#gbx3 {
    background-color: #050505!important;
    color:white!important;}
    .nav-item-container:hover,#nav_collections .nav-item-container.selected:focus, .nav-item-container.selected:focus{
   -moz-transition: background 0.15s linear;
    background-color:#222!important;}
    
.header-tab-title.selected{color:#FF9A5C!important;}
.header-tab-title{color:#FFF!important;}
#gbq1{margin: 0 0 0 6px!important;}


/*body*/
body, #main,.song-row, .song-row td
{
background-color:#000!important;
color:white!important;}

.nav-item-container:hover .playlistDropDown, .nav-item-container:hover .playlistDropDown:hover,
 .nav-item-container.selected:focus .playlistDropDown, .nav-item-container.selected:focus .playlistDropDown:hover,
 .song-row .hover-button[data-id="menu"]:hover
{background-color:#111!important;
-moz-transition: background 0.1s linear;}

/*card*/

.card{background-color:#222!important;
color:white!important;}
.explicit{background-color:#222!important;color:red!important;}
/*playlist sorter*/
.song-table th:hover{background-color:#191919!important;}


/**player**/

#player,.player-middle,.now-playing-menu-wrapper
{background-color:#090909!important;
color:white!important;}
#player .flat-button:hover,#volume:hover, #volume.simulated-hover,#player .rating-container.thumbs li.selected:hover, #player .rating-container.thumbs li:hover
{background-color:#111!important;}

/*slider*/
#played_slider{background-color:#252525!important;}
.playing-progress-background
{background-color:#191919!important;}

/*buttons*/

#player .flat-button[data-id="play-pause"]{
background: url("http://i656.photobucket.com/albums/uu284/UserStyles/sheetv2_zps1221f93a.png?t=1374547621") no-repeat scroll -214px -241px transparent!important;}

#player .flat-button.playing[data-id="play-pause"]
{background: url("http://i656.photobucket.com/albums/uu284/UserStyles/sheetv2_zps1221f93a.png?t=1374547621") no-repeat scroll -138px -243px transparent!important;}

#player .flat-button[data-id="rewind"]{
background: url("http://i656.photobucket.com/albums/uu284/UserStyles/sheetv2_zps1221f93a.png?t=1374547621") no-repeat scroll -50px -242px transparent!important;}

#player .flat-button[data-id="forward"]
{background: url("http://i656.photobucket.com/albums/uu284/UserStyles/sheetv2_zps1221f93a.png?t=1374547621") no-repeat scroll -293px -242px transparent!important;}

#player .flat-button[data-id="repeat"][value="NO_REPEAT"]
{background: url("http://i656.photobucket.com/albums/uu284/UserStyles/sheetv2_zps1221f93a.png?t=1374547621") no-repeat scroll 8px -242px transparent!important;}

#player .flat-button[data-id="shuffle"][value="NO_SHUFFLE"]
{background: url("http://i656.photobucket.com/albums/uu284/UserStyles/sheetv2_zps1221f93a.png?t=1374547621") no-repeat scroll -368px -242px transparent!important;}




img[src$="//ssl.gstatic.com/music/fe/55aee29f6eed41e4538a219f10ca0a63/logo_large.png"]{
height:100px!important; /*width of new image*/
padding-left:234px!important; /*height of new image*/
width:0!important;background: url("http://i656.photobucket.com/albums/uu284/UserStyles/sheetv3_zps88c3aad0.png?t=1374548935") no-repeat scroll -4px 4px transparent!important;}
/**misc**/

/*border*/
*{border-color:rgba(100,100,100,0.2)!important;}

/*remove*/
.fade-out:after
{display:none!important;}
#headerBar
{background:none!important;}
#embed-container{visibility:hidden!important;}

/*font color*/
#gbg6 #gbi4t,#playerSongTitle, #gbg4 #gbgs4d,#headerBar .tab-text,.nav-item-container,
.song-table th,.cluster .header .title,#browse-tabs, #explore-tabs,.card .title
{color:white!important;}

/*time*/
#time_container_duration,#time_container_current
{visibility:visible!important;
background-color:#111!important;
color:white!important;}

/*menu*/
.goog-menuitem{background: none repeat scroll 0 0 rgba(0,0,0,.0)!important;}
.goog-menu,.now-playing-menu, .now-playing-menu .goog-submenu, .now-playing-menu .goog-submenu .goog-submenu-arrow{background: none repeat scroll 0 0 rgba(0,0,0,.7)!important;}
.now-playing-menu .goog-menuitem .goog-menuitem-content, .goog-menuitem-content{    color:white!important;}
    
/*dialog*/    
.simple-dialog{
color:white!important;
background-color:rgba(20,20,20,0.8)!important;}
.simple-dialog-content,.simple-dialog-bg,.simple-dialog-title{
color:white!important;background:none!important;}

textarea.playlist-description,input.playlist-name{
color:white!important;
background-color:rgba(0,0,0,0.8)!important;}



}
