css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("qiushibaike.com") {

/*隐藏右栏*/
.col2 {display: none !important;}

/*左栏加宽*/
.col1 {
  width: 100% !important;
  font-size: 24px !important;
  font-family: 文泉驿微米黑,Arial,黑体;}

/*按钮*/
.bar ul li a {font-size: 16px !important;font-family: Arial;}
.comment,.share { float: right !important;}

/*打开新页面*/
.detail a{margin:-18px 0 0 890px !important;}

/*回复*/
.replay {
    font-size: 24px !important;
    height: 150% !important;
    top: 24px !important;
    width: 90% !important;
    line-height: 1.5em;
}
}
