css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.fortress-forever.com/forum/") {

.bigusername, .bigusername span {
font-family: Tahoma !important;
font-size: 24px !important;
letter-spacing: -2px !important;
}

.body, .page {
width: 100% !important;
}

.body > div[title="return to the forum index"] {
  background-color: #4e5460 !important;
  background-image: none !important;
  height: auto !important;
  border-bottom: 2px solid #353e45 !important;
  color: #fff !important;
  font-size: 40px !important;
  text-align: center !important;
  padding: 20px 40px !important;
  font-family: "Arial" !important;
  letter-spacing: -3px !important;
  font-variant: small-caps !important;
}

.body > div[title="return to the forum index"]::before {
  content: "Fortress Forever" !important;
}

.alt1Active + .alt2 {
  padding: 5px 10px 8px 10px !important;
}
.alt1Active + .alt2 .smallfont {
  width: 330px !important;
  position: relative !important;
}
.alt1Active + .alt2 div[align="right"] {
  position: absolute !important;
  top: 0px !important;
  right: 0px !important;
}

.alt1Active > .smallfont > br + br {
  display: none !important;
}
.alt1Active > .smallfont > br {
  display: none !important;
}
.alt1Active > .smallfont > br + b {
  display: block !important;
  position: absolute !important;
  top: -16px !important;
  right: 0px !important;
}

.alt1Active > .smallfont {
  position: relative !important;
}
.alt1Active > .smallfont > br + b + a, .smallfont > br + b + a + a {
  position: absolute !important;
  display: block !important;
  right: -5px !important;
  top: -24px !important;
  background: #ddd !important;
  width: 150px !important;
  padding: 4px 10px !important;
  text-align: right !important;
  border-left: 1px solid #888 !important;
  border-top: 1px solid #000 !important;
  text-decoration: none !important;
}
.alt1Active > .smallfont > br + b + a + a {
  top: -3px !important;
}
.alt1Active > .smallfont > br + b + a:hover, .smallfont > br + b + a + a:hover {
  color: #000 !important;
  background: #efefef !important;
}

.alt1 > .smallfont > strong {
  font-size: 16px !important;
  display: block !important;
  background: #efefef !important;
  padding: 3px 10px !important;
}
.alt1 > .smallfont[title~="Page"] > strong {
  font-size: inherit !important;
  display: inherit !important;
  background: inherit !important;
  padding: inherit !important;
}
td.alt1 > div > img[border="0"] {
  padding: 3px !important;
  background: #fff !important;
  border: 3px solid #efefef !important;
}
td.alt1 img.inlineimg[border="0"] {
  padding: 0px !important;
  background: none !important;
  border: 0px !important;
}

div.page div div table tbody tr td div div table tbody tr td.alt2,div.page div div table tbody tr td div table tbody tr td.alt2 {
background-color:#ddd !important;
border:2px solid #fff !important
}

div.page div div table tbody tr td div div table tbody tr td.alt2,div.page div div table tbody tr td div table tbody tr td.alt2 img {
width: 100px !important;
}
div.page div div table tbody tr td div div table tbody tr td.alt2,div.page div div table tbody tr td div table tbody tr td.alt2 img.inlineimg {
width: auto !important;
}
div.page div div table tbody tr td div div table tbody tr td.alt2,div.page div div table tbody tr td div[style="margin: 5px 20px 20px;"] div.smallfont[style="margin-bottom: 2px;"] {
  background: #5c6670 !important;
  color: #fff !important;
  padding: 5px !important;
}
div.page div div table tbody tr td div div table tbody tr td.alt2,div.page div div table tbody tr td div > table div.smallfont[style="margin-bottom: 2px;"] {
  background: #ddd !important;
  color: #000 !important;
}

pre.alt2 {
  width: auto !important;
  border: 2px dashed #fff !important;
  background-color: #ddd !important;
  color: #000 !important;
}

.panelsurround .panel div[style="width: 480px;"], .panelsurround .panel div[style="width: 640px;"] {
  width: auto !important;
}

.panelsurround .panel table[style="min-width: 480px; max-width: 100%; width: auto ! important;"] {
  min-width: 100% !important;
}

textarea[name="message"] {
  width: 1000px !important;
}

}
