css:
@-moz-document url-prefix("http://www.bungie.net"), url-prefix("https://www.bungie.net") {
.container_sort { border-top-color: rgb(52,128,23); }
.forumPost .post { border-bottom-color: rgb(52,128,23); }
.topics .followedAuthor { box-shadow: 4px 0 0 #348017 inset; }
.nav_side a:hover { color: #348017; }
.nav_side a:{ color: #254117; }

.on a { background-image: url(http://s15.postimg.org/d6d53vwkn/color.png); }



.Nav_Top {
background: none repeat scroll 0 0 rgba(52,128,23, 0.3);
}

.nav_top .nav_logo .dropdown {
background: url(http://s23.postimg.org/55d26i9g7/Sworburst180.png) no-repeat scroll right center rgba(52,128,23, 0.6);
}

.Nav_Top .menu, .Nav_Top .nav_identity .dropdown {
background: url(http://s23.postimg.org/55d26i9g7/Sworburst180.png) no-repeat scroll right center rgba(52,128,23, 0.6);
}

.Nav_Top .menu li {
background: transparent;
}

.Nav_Top .nav_identity .signedIn .dropdown a:hover{
color: #FFFFFF;
background: none repeat scroll 0 0 rgba(0,0,0, 0.6);
}

.Nav_Top .nav_logo .submenu {
background: none repeat scroll 0 0 rgba(37,65,23, 0.8);
}

.Nav_Top .nav_logo .menu li a {
color: #FFFFFF;
}

.Nav_Top .nav_logo .menu li a:hover{
color: #FFFFFF;
background: none repeat scroll 0 0 rgba(0,0,0, 0.6);
}

.Nav_Top .menu li{
border: none;
}

.nav_logo.opened h1 {
background: #254117;
}

.Nav_Top .opened .signedIn {
background: #254117;
}

.Nav_Top .opened .signedIn a {
color: #FFFFFF;
}

.Nav_Top .opened .signedIn a:hover{
color: #FFFFFF;
background: none repeat scroll 0 0 rgba(37,65,23, 0.6);
}



.container_textfield {
background: none repeat scroll 0 0 rgba(37,65,23, 0.8);
border: none;
}

.container_textarea textarea {
color: #FFFFFF;
}

.container_textbox input {
color: #FFFFFF;
}

.forumPost blockquote {
background: none repeat scroll 0 0 rgba(37,65,23, 0.8);
border-color: #348017 #348017 #348017 rgba(0,0,0,1);
color: #FFFFFF;
}

.forumPost .spoiler{
background: none repeat scroll 0 0 rgba(37,65,23, 0.8);
border-color: #348017 #348017 #348017 rgba(0,0,0,1);
color: #FFFFFF;
}

.forumPost .spoiler:after {
background: none repeat scroll 0 0 #254117;
color: #FFFFFF;
}

.forumPost .spoiler.opened {
background: none repeat scroll 0 0 #254117;
color: #FFFFFF;
}


body{
background: url("http://www.destinythegame.com/content/dam/atvi/bungie/destiny/web/multimedia/citadel.jpg") fixed center top no-repeat #071201;
}

.container_out {
width: 960px;
border: 14px solid rgba(0,0,0,0);
margin: 0 auto;
clear: both;
}


.badge {
background-image: url("http://s13.postimg.org/kcogowpmb/Green_Badge.png");
background-position: right 0;
background-repeat: no-repeat;
background-size: 100% 18px;
color: #FFFFFF;
display: block;
font-size: 10px;
height: 18px;
line-height: 14px;
padding: 0 4px 0 0;
position: absolute;
}

.nav_top .dropTrigger .badge:before {
background-image: url("http://s14.postimg.org/y9ocshsp9/Green_Badge_Point.png");
}

.roster .badge {
background-image: url("http://s13.postimg.org/kcogowpmb/Green_Badge.png");
}

.nav_top .roster .dropTrigger .badge:before {
background-image: url("http://s14.postimg.org/y9ocshsp9/Green_Badge_Point.png");
}


.forumPost .pollHasVoted .PollResults label .bar {
display: block;
background: url("http://s15.postimg.org/d6d53vwkn/color.png");
height: 25px;
position: absolute;
left: 0;
box-shadow: 0px 0px 1px 1px #254117 inset;
z-index: 1;
top: 0;
}


.alert .header {
background: none repeat scroll 0 0 #254117;
height: 15px;
line-height: 15px;
margin-bottom: 15px;
margin-left: -15px;
margin-top: -15px;
padding: 0 15px;
position: relative;
right: 0;
top: 0;
width: 100%;
}



.btn_blue { background-image: -moz-linear-gradient(center top, rgb(52,128,23) 0%, rgb(46,103,18) 100%); border: 0.5px solid rgb(37,65,23); }

.btn_blue { background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, from(rgb(52,128,23)), to(rgb(46,103,18) )); }
.PathView .btn_toEntireTopic{ background: none repeat scroll 0 0 #348017;}
.PathView .btn_toParentPost{ background: none repeat scroll 0 0 #348017;}

.btn_blue { background-image: -o-linear-gradient(90deg, rgb(46,103,18), rgb(52,128,23)); }

.forumPost article h1 > a[href$="mid=920628"]:after {
  background: none repeat scroll 0 0 #3C96DB;
  border: 1px solid #3280BB;
  color: #FFFFFF;
  content: "Bungie";
  display: inline-block;
  font-family: 'OpenSansSemiBold',Arial,sans-serif;
  font-size: 9px;
  line-height: 11px;
  margin-left: 5px;
  padding: 0 2px;
  position: relative;
  text-transform: uppercase;
  top: -1px;
  vertical-align: middle;
}

.nav_sub .btn_preorder {
width: auto;
height: 46px;
display: inline-block;
background: url("http://s13.postimg.org/syzemfp2b/Pre_Order_Button.png") no-repeat center center;
background-size: 100% 41px;
font-family: 'OpenSansSemibold',Arial, sans-serif;
font-szie: 11px;
color: #fff;
line-height: 46px;
text-transform: uppercase;
padding: 0 27px 0 11px;
}

.nav_top_search .btn_search {
background: url("http://s8.postimg.org/vn8ubmebl/Search_Button.png") no-repeat scroll -4px -3px transparent;
float: left;
height: 35px;
width: 35px;
}

.Nav_Top .nav_logo .submenu .header {
line-height: 40px;
height: 40px;
display: block;
font-size: 10px;
font-family: 'OpenSansSemibold', Arial, sans-serif;
padding: 0 21px;
color: #fff;
text-transform: uppercase;
background: url("http://s11.postimg.org/u0juu66fz/Submenu_Header.png");
}

.nav_identity.opened img {
background-color: #348017;
}

.Nav_Top .nav_logo .groups .submenu .btn_groupSearch_submenu {
width: 31px;
height: 30px;
display: block;
background: url("http://s8.postimg.org/vn8ubmebl/Search_Button.png") no-repeat;
float: left;
}
}
