css:
@namespace url(http://www.w3.org/1999/xhtml);
 
@-moz-document domain("theoldreader.com")
{
  .main-container
  {
    padding-top: 0px !important;
  }
  
  .reader .body-fixed-top
  {
    top: 0px !important;
  }
  
  .content-body p
  {
    font-size: 14px !important;
    line-height: 19px !important;
  }

  .navbar,
  .btns,
  a[href^="http://rc.feedsportal.com"]
  {
    display: none !important;
  }

  .container-fluid { top: 0 !important; }
 
  .post
  {
    margin-bottom: 5px !important;
    border: 1px solid #CCCCCC !important;
  }
  
  .content-body a { color: #4D90FE !important; }
  
  .post.unread
  {
    border-left: 2px solid #4D90FE !important;
    padding-left: 10px !important;
  }

  .well { background-color: white !important; }
  
  .label.label-feed { background-color: white !important; }
  
  .label
  {
    background-color: white !important;
    color: #BBBBBB !important;
    text-shadow: 0 0 0 !important;
    margin-bottom: 0px !important;
  }
  
  .page-header .label-inverse a { color: #4D90FE !important; }

  .badge
  {
    background: none !important;
    color: #333 !important;
    font-weight: normal !important;
    font-size: 13px !important;
    padding: 0 !important;
  }
  
  .badge:before { content: "("; }

  .badge:after { content: ")"; }

  .nav-list.feeds-list { margin-left: 8px !important; }
  
  .nav-list { font-size: 13px !important; }
  
  .nav-list li.active a { color: #4D90FE !important; }

  .nav-list li.active a:not(.i):before { border-left: 2px solid #4D90FE !important; }
  
  .slider { background: none repeat scroll 0 0 #58ACFA !important; }
  
  .reader .floating { padding: 0 !important; }
}
