css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix('http://www.tumblr.com/') , url-prefix("https://www.tumblr.com/") {

     /*   
	 Dashboard by http://dear-poop.tumblr.com
     Enjoy~ (✿◠‿◠)
	 */

	 
	 
	 
div#right_column,
div#right_column:after {background: none !important;}	 
	 
body { background-image:url('http://electric.curves.free.fr/tumblr/dash/26%20-%20Taiga/Fond.png') !important;
background-color:#fff !important;
background-repeat:repeat !important;
background-position: center top !important;
background-attachment:fixed !important;
color: #5c3a46 !important;
opacity: 1 !important;
font-size: 12px !important;
font-family: 'Trebuchet MS' !important;
text-align: justify !important;}

h1, h2, #footer {color:white !important; font-family: 'Trebuchet MS' !important;}

img {-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}
    
img:hover {opacity:0.6 !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}   

a:link, a:active, a:visited {color: #c16b8a !important; text-decoration: none !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;} 
a:hover {color: #a32b42 !important; text-decoration: none !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;} 

.post_info a:link, .post_info a:active, .post_info a:visited {color: #a32b42 !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear; text-decoration: none !important;} 
.post_info a:hover {color: #c16b8a !important; text-decoration: none !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;} 

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {background: transparent !important;}
.post_info {color: #5c3a46 !important;
  font-size: 13px !important;}
.post_content {font-size: 12px !important;}


  
.post_question {font-family: 'century gothic', sans-serif !important; font-size: 16px !important; 
line-height: 20px !important;
background: -moz-linear-gradient(bottom, #c26b8a 0%, #eab1c5 100%) !important;
background: -webkit-linear-gradient(bottom, #c26b8a 0%, #eab1c5 100%) !important;
background: -o-linear-gradient(bottom, #c26b8a 0%, #eab1c5 100%) !important;
color: white !important; box-shadow: 0px 0px 5px #414141 !important; text-shadow: 0px 0px 2px #fff !important;}


pre {background:transparent !important;}
blockquote {border: 2px solid #957a8d !important; padding: 10px 10px 15px 15px !important;
background: -moz-linear-gradient(bottom, #957a8d 0%, #fff 100%) !important;
background: -webkit-linear-gradient(bottom, #957a8d 0%, #fff 100%) !important;
background: -o-linear-gradient(bottom, #957a8d 0%, #fff 100%) !important;
border-radius: 20px; text-shadow: 0px 0px 5px #fff;  margin: 0 !important; font-size:11px !important;}


/*[[topbar]]*/



/*tumblr logo*/

#logo {
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  margin-left: 60px !important;
  padding-left: 200px !important;
  padding-top: 100px !important;
  background-image: url("http://electric.curves.free.fr/tumblr/dash/26%20-%20Taiga/Logo.png") !important;
  background-repeat: no-repeat !important;}
  
  




/*new post icons bar*/

ol#posts #new_post {
background: #ffffff url('http://electric.curves.free.fr/tumblr/dash/26%20-%20Taiga/Navig.png')  top center no-repeat !important;
box-shadow: 0px 0px 10px #fff !important;
border: 0px solid #775e6d !important;
border-radius:20px !important; height: 100px !important;
text-align:center !important;
margin:0px !important; padding-bottom:0px !important; padding-right:0px !important;}

.new_post_label {
font-size:12px!important; padding-top:80px !important; 
font-family:'georgia' !important; text-transform:uppercase!important; 
text-shadow: 0px 0px 3px #fff !important;}

.new_post_label > I {background-image:none !important;}
.new_post_label > I:before {content: none !important;}



.notification  {font-family:'trebuchet ms' !important;
background: -moz-linear-gradient(bottom, #9f7683 0%, #fff 100%) !important;
background: -webkit-linear-gradient(bottom, #9f7683 0%, #fff 100%) !important;
background: -o-linear-gradient(bottom, #9f7683 0%, #fff 100%) !important;
 border: none !important; text-transform:uppercase!important;
color: #605e5e !important; font-size: 9px !important;}
.notification :link, .notification :active, .notification :visited {color: #883958 !important; text-decoration: none !important; border: none !important;
}



.avatar_frame {border: 3px solid #a72b48 !important;box-shadow: 0px 0px 2px #a72b48 !important;}

.post_avatar {border: 3px solid #fff !important;
border-radius:10px !important;
box-shadow: 0px 0px 2px #a72b48 !important;}


/* Place, forme des images */
.image { display: block !important;
margin-left: auto !important;
margin-right: auto !important;
border-radius:0px !important;}

.photoset img {border-radius:0px !important;}



/* RETURN TO TOP */
div.return_to_top_icon{	background: none !important;
background-image: url('http://electric.curves.free.fr/tumblr/dash/26%20-%20Taiga/Top.png') !important;
width: 71px !important;
height: 104px !important;
opacity: 1 !important;
margin-top: 0px !important;
border-bottom: solid 0px #fff !important;
border-right: 0px solid transparent !important;
border-left: 0px solid transparent !important;}






/*Colonne droite*/

.controls_section {
background: -moz-linear-gradient(bottom, #dbd7d6 0%, #fff 100%) !important;
background: -webkit-linear-gradient(bottom, #dbd7d6 0%, #fff 100%) !important;
background: -o-linear-gradient(bottom, #dbd7d6 0%, #fff 100%) !important; }

.controls_section a {color:#7b4c51 !important;}

LABEL, .count {color: #7b4c51 !important;}






/*tags*/

.post_tags a:hover {-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear; background:#be5477 !important; cursor:help; 
padding: 0px; color:white !important; border-radius: 0px;}

.post_tags a {font-size:9px!important;margin-top:0px!important; letter-spacing: 0px !important;
font-family:'trebuchet ms';vertical-align:middle !important; text-transform:uppercase  !important;}

.source_url{font-size:9px!important;
text-transform:uppercase;
font-family:'trebuchet ms'!important;
vertical-align:middle !important;
margin-right:4px!important;}
.footer_links{margin-top:20px!important;
vertical-align:middle !important;}

.post_info A,
.post_controls A,
.footer_links.with_tags SPAN A {
color:#333333 !important;}

.token {border: 0px solid #444 !important; 
background-color:#d1d9db!important;}



/* Charger posts */

#previous_page_link, #next_page_link, .add_and_remove,
.mass_editor, .MissingE_marklist_title, .customize,
.recessed, .answer_avatar, .flat, .dashboard_options_form,
#auto_pagination_loader{background:#FFFFFF !important; color: #be5477 !important;
text-shadow: none !important;}




/*.selection.nipple - dashboard, user, inbox - "white pages"*/

/*.selection_nipple.white - create new blog, help, preferences*/

/*SELECTION ARROW begin - points to dashboard selection*/
.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
  background: none !important;
  width: 0px !important;
  height: 0px !important;
  margin-top: 0px !important;
  border-bottom: solid 4px #ffffff !important;
  border-right: 11px solid transparent !important;
  border-left: 11px solid transparent !important;
}

.selection_nipple:not([class="selection_nipple white"]) {
  border-bottom: solid 11px #ffffff !important;}

/*SIDE ARROWS*/
.arrow {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 9px #ffffff !important;
border-bottom: 9px solid transparent !important;
border-left: 2px solid transparent !important;
border-top: 9px solid transparent !important;
margin-top: 0px !important;
}
.nipple {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 6px #ffffff !important;
border-bottom: 6px solid transparent !important;
border-left: 0px solid transparent !important;
border-top: 6px solid transparent !important;
margin-top: 0px !important;
}
.nipple.border {
background: none !important;
width: 0px !important;
height: 0px !important;
border-right: solid 6px #ffffff !important;
border-bottom: 6px solid transparent !important;
border-left: 1px solid transparent !important;
border-top: 6px solid transparent !important;
margin-top: 1px !important;}









/*text post message body background*/

.mceContentBody {
  background: white !important;
  color: #727272 !important;
  /*font-family: georgia !important;*/
  font-size: 12px !important;}

#new_post .arrow {  margin-left: -0px !important;}

 }

style_setting_options.value:
#header {z-index: 777 !important; position:fixed !important; width: 900px !important; 
height:80px !important;
margin:0px !important; padding:0px !important; background-attachment:fixed !important;
background-position:center top !important;
background: url('http://electric.curves.free.fr/tumblr/dash/26%20-%20Taiga/Fond.png') top center !important; }

#right_column {padding-top: 100px !important;}
#left_column {padding-top: 100px !important;}
