@-moz-document domain("www.ygosu.com") {
/* ygosu */
::selection {
    color: #fff;
    background: #FF1744;
}
body {
    font-family: 'Noto Sans KR', sans-serif;
    font-size: 14px;
    background: #e5e5e5;
}
#contain {
    width: 800px;
    margin: 2em auto;
}

/* article */
.board_body {
    border: none;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.25);
    border-radius: 3px;
    background: #fff;
}

/* article header */
.board_top .info .img {
    width: 7em;
    height: 7em;
    overflow: hidden;
    border-radius: 50%;
}
.board_top .info .img a img {
    width: 100px;
    height: inherit;
}

/* article content */
.container {
    font-family: 'Noto Sans KR', sans-serif;
    font-size: 14px;
}
.container p {
    margin: .6em 0;
}
.board_wrap {
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.25);
    border-radius: 3px;
    background: #fff;
}

/* article list */
.board_left {
    width: 800px;
    margin: 0;
}
table.bd_list thead tr {
    background: none!important;
}
table.bd_list thead tr a {
    color: #212121!important;
}
table.bd_list tbody td {
    background: none;
}
table.bd_list tr td,
table.bd_list tr td a {
    padding: 1em .5em;
    font-size: 1em!important;
}
table.bd_list thead th:nth-child(4),
table.bd_list tbody td.view,
table.bd_list thead th:nth-child(6),
table.bd_list tbody td.vote,
table.bd_list thead th:nth-child(7),
table.bd_list tbody td.read,
table.bd_list tbody td.like {
    display: none;
}
table.bd_list thead th:nth-child(3),
table.bd_list tbody td.name {
    width: 12em;
    overflow: hidden;
    text-overflow: ellipsis;
    white-space: nowrap;
}
table.bd_list tbody td.date {
    width: 5em;
}
table.bd_list tbody td.tit {
    background-position: .5em 1.5em;
}
table.bd_list tbody td.tit a {
    background: none;
}
table.bd_list tr.selected td,
table.bd_list tr:hover td,
table.bd_list tr.selected td a,
table.bd_list tr:hover td a,
table.bd_list tr.selected td a span {
    background: #F44336;
    color: #fff!important;
}

/* footer */
.paging .num a,
.paging .num b {
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.25) inset;
    width: 3em;
    height: 3em;
    display: inline-block;
    background: #eee;
    border-radius: 50%;
    border: none;
    line-height: 3;
    margin: 0.25em;
    padding: 0;
}
.paging .num b {
    background: #fff;
}

/* 불필요한 요소 삭제 */
.right_wrap,
#reply,

/* .reply.bestreply, */
.header,
.btn_bottom,
.pop_board > .tab_1,
.pop_board > .selection,
#footer,
.top_gnb2,
.gnb,
.side_menu,
.bd_tophead,
.ad700,
iframe[id*="realssp_ygosu"] {
    display: none!important
}
}

@-moz-document domain("gall.dcinside.com") {
/* dcinside */
/* font */
html,
body,
div,
span,
iframe,
h1,
h2,
h3,
h4,
h5,
h6,
p,
a,
em,
img,
strong,
b,
u,
i,
center,
dl,
dt,
dd,
ol,
ul,
li,
fieldset,
form,
label,
legend,
table,
caption,
tbody,
tfoot,
thead,
tr,
th,
td,
article,
aside,
embed,
figure,
figcaption,
footer,
header,
nav,
section,
summary,
audio,
video,
button {
    font-family: 'Noto Sans KR', sans-serif!important;
    font-size: 14px!important;
}

/* reply */
.cmt_mdf_del button {
    color: rgba(0, 0, 0, 0)!important;
}

/* list */
section.left_content {
    width: 100%!important;
}
table.gall_list td {
    padding: 1em 0!important;
}
table.gall_list td.gall_num {
    width: 3em;
}
table.gall_list caption {
    display: none;
}
table.gall_list colgroup > col:nth-child(1) {
    width: 6em!important;
}
table.gall_list colgroup > col:nth-child(3) {
    width: 7em!important;
}
table.gall_list colgroup > col:nth-child(4) {
    width: 5em!important;
}
table.gall_list colgroup > col:nth-child(5) {
    width: 4em!important;
}
table.gall_list colgroup > col:nth-child(6) {
    width: 3em!important;
}
.list .gall_tit a {
    width: 100%!important;
    max-width: 100%!important;
}
.list .gall_tit a.reply_numbox {
    display: none;
}
.right_content {
    display: none
}
}

@-moz-document domain("todayhumor.co.kr") {
/*todayhumor */
/* 불필요한 요소 삭제 */
.memoContainerDiv[style*="background:#fff;"],

/*메달 없는 리플*/
.okListDiv,

/*게시물 추천한 사람 목록*/
.viewHorizonBar,

/*촌스러운 수평선*/
.viewScrapDiv,

/*촌스러운 스크랩 버튼*/
.okNokBookDiv,

/*촌스러운 추천, 반대 아이콘*/
hr,
td[bgcolor="0089BC"],
[style*="bottomtable_shadow.gif"],
#topmenu_shadow_line,
[style*="#4d6c77"],
div[class^=topmenu_line],

/*header menu*/
div[class="memoDiv"],

/* 댓글 */
div[class="memoDiv rereMemoDiv"],

/* 댓글 */
.memoShadowDiv,

/* 댓글 */
.viewHorizonBar + div,

/* 푸르딩딩 안내 (코딩 실력 개극혐) */
tr.list_tr_sisa,

/*시사게*/
img[src^="/board/ima"],

/* 리플추천버튼 */
div[style*="padding:30px 0 30px ;margin:20px 0 10px ;"],

/*리플 로그인 설명*/
.ad_adsense,

/*애드센스*/
.writerInfoBgMiddle,
.writerInfoBgLeft,
.writerInfoBgRight,
.writerInfoContainer,

/*글 정보*/
.tailDiv/*꼬릿말*/
{
    display: none;
    height: 0!important;
}

/* 촌스러운 요소 편집 */
body,
h1,
h2,
h3,
h4,
input,
button,
textarea,
div,
select {
    font-family: 'Noto Sans KR', sans-serif;
    font-size: 14px;
}
.viewSubjectDiv,
#topmenu_container2,
#topmenu_container1 {
    background: none !important;
}
.whole_box,
[style*="009DBC"],
[bgcolor*="009dbc"],
[bgcolor*="4D6C77"] {
    background-color: white !important;
}

/*촌스러운 배경*/
a:link,
a:visited {
    color: #000 !important;
}

/*앵커 색상 수정*/
a {
    font-family: 맑은 고딕 !important;
}

/*글꼴수정*/
tr:nth-child(4n) {
    background: #eee;
}

/*테이블 색*/
tr[bgcolor="#F1F2F1"] {
    background: #faa !important;
}
.tcx_image,
.viewContent {
    border: none!important;
}

/* 댓글 */
.member_icon[src="http://www.todayhumor.co.kr/member/images/icon_ribbon.gif"] {
    display: none;
}
.rereMemoWrapperDiv {
    border: none;
}
.memoWrapperDiv {
    border-top: 1px solid #eee;
}
.memoWrapperDiv:first-child {
    border: none;
}

/* 간지 input 스타일 */
input {
    border: 1px solid silver;
    border-radius: 3px;
    background: #f0f0f0;
    padding: 2px 5px;
}
select {
    border: 1px solid silver;
    border-radius: 3px;
    background: #f0f0f0;
    padding: 1px 1px 1px 5px;
}
input:hover,
select:hover,
textarea:hover {
    border-color: gray;
}
input:focus,
select:focus,
textarea:focus {
    background: #fee;
    outline: none;
}
textarea {
    padding: 5px;
    border: 1px solid silver;
    background: #f0f0f0;
    border-radius: 3px;
}
input[type="button"],
input[type="submit"] {
    color: #fff;
    background: #aaa;
    padding: 2px 5px;
    border: 1px solid gray;
    border-radius: 3px;
}
input[type="button"]:hover,
input[type="submit"]:hover {
    background: gray;
    border-color: #F44336;
}
}
