css:
@-moz-document url-prefix('http://www.tumblr.com/'), url-prefix('https://www.tumblr.com/')  

{

.ui_notes .ui_note .part_main .summary
{
margin-bottom: 6px !important;
}
    
.ui_stats .row .unit .brag 
{
font-size: 23px !important;
line-height: 22px !important;
}

.ui_stats .unit .stat_txt
{
padding: 13px 5px 0px !important;
height: 44px !important;
}

.ui_stats .stat_title 
{
font-size: 10px !important;
}

.ui_tops .tops_box .tops_header h1
{
    float: left;
    font-size: 10px !important;
    line-height: 11px !important;
    letter-spacing: 0.05em;
    margin: 5px 2px 15px !important;
    color: rgb(68, 68, 68);
    opacity: 0.9;
}

.ui_jumbo_avatar .avatar_image 
{
width: 55px !important;
height: 55px !important;
margin-bottom: 3px !important;
}

.ui_jumbo_avatar 
{
width: 55px !important;
font-size: 9px !important;
margin: 0px 7px 0px 28px !important;
padding: 0px 0px 0px !important;
}

.ui_tops .tops_box 
{
min-height: 110px !important;
}

.ui_tops .tops_box .tops_header .top_notes 
{
font-size: 9px !important;
line-height: 13px !important;
margin: 3px 0px 0px !important;
}

.ui_tops .tops_box .tops_header 
{
margin: 0px 0px 2px !important;
height: 17px !important;
}

.ui_tops .post 
{
width: 100px !important;
height: 67px !important;
min-width: 100px !important;
min-height: 67px !important;
margin: 0px 33px 0px !important;
}

.ui_tops .tops_box 
{
min-height: 98px !important;
padding: 0px 0px 0px 8px !important;
margin: 0px 5px 0px !important;
}
     
.ui_notes .part_icon .note_follow
{
margin-top: -3px !important;
}

.ui_notes .ui_note .part_ignore
{
width: 10px !important;
height: 10px !important;
}
     
.ui_notes_switcher 
{
line-height: 15px !important;
font-size: 10px !important;
margin: 0px 5px 0px !important;
}

.notifications_block.ui_notes_switcher .part-toggle 
{
padding: 0px 0px 0px 15px !important;
}

.ui_note div.retags
{
padding: 23px 50px 14px !important;
line-height: 9px !important;
}
     
.ui_notes .date_header, .date_header 
{
line-height: 12px !important;
font-size: 9px !important;
margin: 0px 5px 9px !important;
}

     
.ui_notes .ui_note .stage 
{
min-height: 30px !important;
}

.ui_notes .ui_note 
{
min-height: 20px !important;
font-size: 11px !important;
}

.ui_notes .ui_note .part_activity 
{
line-height: 20px !important;
}

.notification .avatar, .notes .note .avatar, .ui_notes .frame .avatar, .ui_post_badge 
{
width: 20px !important;
height: 20px !important;
margin: 0px 0px 0px !important;
}

.ui_notes .ui_avatar, .ui_notes .ui_note .part_icon 
{
width: 20px !important;
height: 20px !important;
margin: -5px 0px 0px !important;
}

.ui_notes .avatar .inner_frame, .ui_post_badge .inner_frame 
{
width: 18px !important;
height: 18px !important;
}

.xkit-activity-plus-timestamp 
{
font-size: 9px !important;
padding: 2px 50px 0px !important;
margin: -15px 0px 0px !important;
}

#xkit-activity-plus-note-filter 
{
margin-top: -18px !important;
}
}
