css:
@-moz-document domain("rubydoc.info") {
body {
    margin: 0;
    padding: 0;
}

/* eliminate whitespace at top of page */
.frames #content h1 {
  margin-top: 0 !important;
}

#content h2 {
  margin-top: 1em;
  margin-bottom: .5em;
  font-weight: bold;
  font-size: 24px;
}

#content h2 small {
  float: none !important;
  display: inline !important;
  font-size: 11px !important;
}

/* use serif font for text */
div#body > div#content > div#filecontents,
div#filecontents {
  font-family: 'palatino linotype', serif;
  font-size: 15px;
  color: #444;
  padding-left: .5em;
  padding-right: .5em;
}

/* use georgia for headers */
div#filecontents h1,
div#filecontents h2,
div#filecontents h3,
div#filecontents h4,
div#filecontents h5,
div#filecontents h6 {
  font-family: georgia;
  font-weight: bold;
}
div#filecontents h1 {
  font-size: 2em;
}

#filecontents ul,
.docstring ul {
    margin-left: 1em;
    margin-right: 1em;
}

#filecontents li > p,
.docstring li > p {
    margin-top: .5em;
    margin-bottom: .5em;
}

#body {
    padding: 0 20px 20px 20px;
}

#header {
    padding: 10px 20px 10px 20px;
    background-color: #EEEEEE;
    margin-bottom: 10px;
}

#toc {
    font-family: 'lucida grande', sans-serif;
    padding: 10px;
    background-color: #EEEEEE;
    border-radius: 6px;
    border: none;
}

#full_list {
    font-family: 'lucida grande', sans-serif;  
    font-size: 11px;
}

#full_list li {
  padding-top: 2px !important;
  padding-bottom: 2px !important;
}

#full_list li,
#toc ol,
#toc > ol > li,
#toc li,
#toc a {
    font-family: inherit;
    font-size: inherit;
}

#footer {
    border-top: none;
    padding: 10px 20px 10px 20px;
    background-color: #EEEEEE;
    margin-top: 10px;
}

/* Box */

#filecontents dl.box,
dl.box {
    display: table;
    border-top: 1px #c6c6c6 dotted;
    margin: 0;
    padding-left:0px;
    width: 100%;
}

#filecontents div.box-additions dl.box,
div.box-additions dl.box {
    border-top: none;
}

#filecontents dl.box div.row,
dl.box div.row {
      display: table-row;
      margin: 0px;
      padding: 2px 0px 2px 0px;
      line-height:1.5em;
}

#filecontents dl.box dt,
dl.box dt {
      display: table-cell;
      background: none !important;
      border: 0px ;
      border-bottom: 1px #c6c6c6 dotted !important;
      white-space: nowrap;
      font-weight: bold;
      padding: 1px 10px;
      width: 25%;
      overflow: visible;
      text-align: left;
      float: none;
}

#filecontents dl.box dd,
dl.box dd {
      display: table-cell;
      background: none !important;
      border: 0px ;
      border-bottom: 1px #c6c6c6 dotted !important;
      margin:0;
      padding: 1px 0px 1px 3px;
      float: none;
      width: 74%;
}

dl.box > div > dd,
ul.fullTree,
ul.fullTree > li {
  font-family: monospace !important;
  font-size: 13px;
}

/* Headlines */

h1 {
    border-top: none;
}

div.discussion {
  margin-left: auto;
  margin-right: auto;
  width: 90%;
}

h2 {
  margin: 3em 0 0.5em 0;
  font-size: 1.5em;
  /*border-top: 4px #EEEEEE solid;*/
  border-bottom: 0px;
  padding: 10px 0 3px 0;  /*fuer die Linie */
}

.docstring h3 {
  margin: 1.5em 0 0.3em 0;
  font-size: 1.0em;
}


.docstring h4 {
  margin: 1.3em 0 0.2em 0;
  font-size: 1.0em;
}

p.signature,
h3.signature {
    background-color: #f1f1f1 !important;
    font-family: monospace, Courier, Monaco,Consolas;
    font-size: 1.1em;
}

.method_details {
    border-top: 0px;
    margin-top: 50px;
    padding-top: 0;
    clear:both;
}

.method_details.first {
    margin-top: 15px;
}



/* method summary: show as table */

ul.summary  {
  display: table;
  border-top: 1px #c6c6c6 dotted;
  margin: 2px 0 5px 0;
  padding-left:0px;
  width: 100%;
}

ul.summary li.public {
  display: table-row;
  min-height: 28px;
  height: 28px;
  line-height: 28px;
  vertical-align: middle;
  margin: 0px;
  /* padding: 1px 0px 1px 0px; */
}


ul.summary a:link,
ul.summary a:visited {
    font-size: 1.0em;
    font-weight:normal !important;
}

.summary .summary_signature {
    display: table-cell;
    background: none;
    border: 0px;
    padding: 1px 10px;
    border-bottom: 1px #c6c6c6 dotted;
    white-space:nowrap;
    width: 25%;
    overflow: visible;
}

.summary .summary_desc {
    display: table-cell;
    margin:0px;
    border-bottom: 1px #c6c6c6 dotted;
    font-family: "Lucida Sans","Lucida Grande",Verdana,Arial,sans-serif;
    font-size: 13px;
    width: 74%;
}

p.signature, h3.signature {
    background: none;
    border: none;
    border-top: 1px #c6c6c6 dotted
}


/* method details */

.note.returns_void {
    display :none;
}


.showSource {
    display: none;
    float: right;
}

table.source_code {
    margin-left: 100px;
    border-left: 0px;
    display: table !important;
}

/* rdoc def list as table */

dl.rdoc-list {
    display: table;
    border: none !important;
    border-top: 1px #c6c6c6 dotted !important;
    padding-left:0px;
    margin: 2px 0 5px 0;
    width: 100%;
}

div.rdoc-dl-row {
    display: table-row;
    margin: 0px;
    padding: 2px 0px 2px 0px;
    line-height:1.5em;
}


dl.rdoc-list dt {
    display: table-cell;
    background: none !important;
    border: 0px;
    padding: 1px 10px !important;
    border-bottom: 1px #c6c6c6 dotted;
    white-space:nowrap;
    font-weight: normal !important;
    width: 25%;
    overflow: visible;
}

dl.rdoc-list dd {
    display: table-cell;
    background: none;
    border-bottom: 1px #c6c6c6 dotted;
    margin:0 !important;
    padding: 1px 0px 1px 3px !important;
    width: 74%;
}

/* bullet lists */
.docstring ul {
  margin: 2px 0 5px 0;
}


/* monospace font*/
.object_link{
    font-family: monospace;
}

/* Constants as table */

dl.constants {
    display: table;
    border: none !important;
    border-top: 1px #c6c6c6 dotted !important;
    padding-left:0px;
    margin: 2px 0 5px 0;
    width: 100%;
}

div.constants-dl-row {
    display: table-row;
    margin: 0px;
    padding: 2px 0px 2px 0px;
    line-height:1.5em;
}

dl.constants dt {
    display: table-cell;
    background: none !important;
    border: 0px;
    padding: 1px 10px !important;
    border-bottom: 1px #c6c6c6 dotted;
    white-space:nowrap;
    font-weight: normal !important;
    font-family: monospace;
    width: 25%;
    overflow: visible;
}

dl.constants dd {
    display: table-cell;
    background: none;
    border-bottom: 1px #c6c6c6 dotted;
    font-family: monospace;
    margin:0 !important;
    padding: 1px 0px 1px 3px !important;
    width: 74%;
}

dd pre {
    padding: 2px 0 2px 4px !important;
}
table.source_code tbody td,
table.source_code tbody td pre,
#content .method .synopsis,
pre.lines  {
  margin: 0 !important;
  padding: 0 !important;
  font-family: monospace !important;
  font-size: 13px !important;
  font-style: normal !important;
  font-weight: normal !important;
  line-height: 1.2 !important;
}

table.source_code tbody td > pre,
table.source_code tbody td > pre.code {
  border: none;
}
.source_code pre.lines {
  padding-left: 2px !important;
  padding-right: 2px !important;
}

pre.ruby,
pre.code,
#content pre.ruby,
#content pre.code {
    background-color: #3A3A3A;
    color: #F5F5F5;
    font-family: monospace;
    font-size: 13px;
    font-style: normal;
    font-weight: normal;
    line-height: 1.2;
}

pre.ruby,
#content pre.ruby {
  border: 4px solid #E3E3E3;
  border-radius: 4px 4px 4px 4px;
  margin-left: auto;
  margin-right: auto;
  width: 95%;
  white-space: pre-wrap;
  word-wrap: break-word;
  overflow-x: auto;
  padding: 4px 6px;
}

pre.ruby .ruby-keyword,
pre.code .kw {
    color: #44a6ff !important;
    font-style: normal;
    font-weight: normal;
}

pre.ruby .ruby-comment,
pre.code .comment,
pre.code .info,
pre.code .info.file {
    color: #FFB7DB !important;
    font-style: normal;
    font-weight: normal;
}

pre.ruby .ruby-symbol,
pre.code .symbol {
    color: #60FF60 !important;
    font-style: normal;
    font-weight: normal;
}

pre.ruby .ruby-constant,
pre.code .const {
    color: #ddb286 !important;
    font-style: normal;
    font-weight: normal;
}

pre.ruby .ruby-operator,
pre.code .op,
pre.code .rparen,
pre.code .lparen,
pre.code .rbracket,
pre.code .lbracket {
    color: #FFFDAE !important;
    font-style: normal;
    font-weight: bold;
}

pre.ruby .ruby-identifier,
pre.code .id {
    color: #42CAD9 !important;
    font-style: normal;
    font-weight: normal;
}

pre.ruby .ruby-string,
pre.ruby .tstring,
pre.code .tstring,
pre.ruby .tstring_content,
pre.code .tstring_content,
pre.code .string {
    color: #ccb3e0 !important;
    font-style: normal;
    font-weight: normal;
}

}
