css:
@-moz-document url-prefix("https://www.google.com/calendar/"), url-prefix("http://www.google.com/calendar/") {
 *{
        font-family: "segoe ui" !important;
	    font-size: 12px !important;
	    font-weight: 100 !important;
}
#gbx1, #gb #gbx1, #gbq, #gbu, #gb #gbq, #gb #gbu {
    background: url("https://lh5.googleusercontent.com/-02LQgucdgGk/UIbjqcrVU8I/AAAAAAAABmE/PuQ5PGX0SY4/s168/Skysa-BlackBar.png") repeat scroll 0 -0px black !important;
}
#mainnav {
    background: url("https://lh5.googleusercontent.com/-02LQgucdgGk/UIbjqcrVU8I/AAAAAAAABmE/PuQ5PGX0SY4/s168/Skysa-BlackBar.png") repeat scroll 0 -4px black !important;
    height: 32px !important;

}
#vr-nav {
    background: none repeat scroll 0 0 #000000 !important;
    border-bottom: 1px solid #333333 !important;
}
#gridcontainer {
    background: none repeat scroll 0 0 #000000 !important;
}
.date-top {
    color: #FFFFFF !important;
}
.trans-strip {
	display: none !important;
}
.button-strip {
    margin: 1px 0 0 16px !important;
}
.goog-imageless-button, .navbuttonouter {
	background: url("https://lh5.googleusercontent.com/-02LQgucdgGk/UIbjqcrVU8I/AAAAAAAABmE/PuQ5PGX0SY4/s168/Skysa-BlackBar.png") repeat-x scroll 0 -6px transparent !important;
	color: #b5b5b5 !important;
}
.goog-imageless-button-collapse-right {
    margin-right: 1px !important;
}
.goog-imageless-button-checked {
    color: #ffffff !important;
    border: 1px solid #333333 !important;
}
#calcontent.eui-s #mainbody {
    background: none repeat scroll 0 0 #000000 !important;
}
#calcontent.eui-t #vr-nav {
    padding-bottom: 1px !important;
}
#mainbody {
    background: none repeat scroll 0 0 #000000 !important;
}
.mv-event-container {
    background: none repeat scroll 0 0 #111 !important;
    border-top: medium none !important;
}
.mv-daynames-table {
    background: none repeat scroll 0 0 #000000 !important;
    color: #B5B5B5 !important;
}
.mv-dayname {
    text-align: center !important;
}
.mv-container {
    background: none repeat scroll 0 0 #000000 !important;
}
.st-dtitle {
    background: linear-gradient(to bottom, #111111 0px, #000000 100%) repeat scroll 0 0 transparent !important;
	background: -webkit-gradient(linear, left top, left bottom, from(#111), to(#000)) !important;
    border-left: 1px solid #333333 !important;
    border-top: 1px solid #333333 !important;
    border-bottom: 1px solid #333333 !important;
    color: #b5b5b5 !important;
}
.st-bg {
    border-left: 1px solid #333333 !important;
}
#rhstogglecell {
    background-color: #1A1A1A !important;
}
.mv-event-container {
    border-right: 1px solid #333333 !important;
	border-bottom: 1px solid #333333 !important;
}
/*search box*/
.gbqfqw {
    background: none repeat scroll 0 0 #333333 !important;
    border-color: #000000 !important;
}
.gbqfif, .gbqfsf {
    color: #FFFFFF !important;
    font: 16px arial,sans-serif;
}
.gbqfb {
    background: url("https://lh5.googleusercontent.com/-dPCCcq0x-jQ/UMzfVd4nMcI/AAAAAAAAChE/3bov3Ate1nE/s69/button_background_black_shinny.png") repeat-x scroll 0 0 transparent !important;
    border: 1px solid #000000 !important;
}
#gbgs3 {
    background: url("https://lh5.googleusercontent.com/-dPCCcq0x-jQ/UMzfVd4nMcI/AAAAAAAAChE/3bov3Ate1nE/s69/button_background_black_shinny.png") repeat-x scroll 0 0 transparent !important;
    border: 1px solid #000000 !important;
}
#gbgsa {
    display: none !important;
}
#gbgsi {
    background-position: 0 0 !important;
}
#gbgs4, .gbgs {
    background: url("https://lh5.googleusercontent.com/-dPCCcq0x-jQ/UMzfVd4nMcI/AAAAAAAAChE/3bov3Ate1nE/s69/button_background_black_shinny.png") repeat-x scroll 0 0 transparent !important;
    border: 1px solid #000000 !important;
}
#gbg6 #gbi4t, #gbg4 #gbgs4d {
    color: #b5b5b5 !important;
}
.new-onegpad #gb {
    background: none repeat scroll 0 0 #000000 !important;
}
/*navigation bar*/
#calcontent.eui-t #vr-nav {
    margin-top: 0px !important;
    padding-bottom: 0px !important;
    border-top: .1px solid #333333 !important;
}
/*BEGIN - Based on Autohide Tool Palette by aftermarketgirl*/

/* Get rid of the Google-branded search area header */
#vr-header
{
     display: none !important;
}

/* Hide the tools in the palette */

#mothertable > #nav
{
z-index:2;
-moz-transition-duration: .2s;
-webkit-transition-duration: .2s;
position:fixed !important;
width: 200px !important;
height: 100% !important;
padding: 30px !important;
background-color: white !important;
opacity: .2 !important;
left: -275px !important;
border-right: #000 5px solid !important;
margin-left: 20px !important;
margin-top: 69px !important;
}

/* Show the tools in the palette on hover */

#mothertable > #nav:hover {
-webkit-transition-duration: .2s;
-moz-transition-duration: .2s;
position:fixed !important;
z-index:2;
opacity: 1 !important;
	-moz-box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
	box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
	-webkit-box-shadow: 4px 4px 6px rgba(0,0,0, 0.6) !important;
left: -30px !important;
}

/* Move the main calendar over to the left */
div#mothertable > div#maincell > div#mainbody
{
     margin: 0 !important;
}
/*END - Based on Autohide Tool Palette by aftermarketgirl*/

/*Get rid of those stripes*/
.rb-ro-10, .rb-ro-15, .rb-ro-20  {
    background-image: none !important;    
}
#mainlogo {
    color: #1691FF !important;
}
#gbx1, #gbx2 {
border-bottom: 1px solid #333 !important;
border-color: #333 !important;
}
/*navigation images*/
.navForward {
    background: url("https://lh4.googleusercontent.com/-tdLF70WXwOU/UewwyKzjQuI/AAAAAAAADOM/wPSVweV2ti0/s14/white_right_arrow.png") no-repeat scroll 14px 2px transparent !important;
}
.navBack {
    background: url("https://lh4.googleusercontent.com/-A6Tc85WWatI/UewwyNQn1qI/AAAAAAAADOA/1tIrmqqVw9Y/s13/white_left_arrow.png") no-repeat scroll 14px 2px transparent !important;
}
#mg-settings .goog-imageless-button-outer-box {
    background: url("https://lh3.googleusercontent.com/-eqjSZ6p0ghw/UewwyMlOLqI/AAAAAAAADOI/AKCTdNDlRM0/s17/white_settings_gear.png") no-repeat scroll 6px 0 transparent !important;
}
#mg-more .more-arrow {
    background: url("https://ssl.gstatic.com/ui/v1/zippy/arrow_down_white.png") no-repeat scroll 0 1px transparent !important;
}
.gb_ea .gb_ca {
    background-image: url("data:image/png;base64,") !important;
}
/**/
.snt-container {
    background-color: #000000 !important;
    border-color: #000000 -moz-use-text-color #000000 #000000 !important;
    border-bottom-color:#000000 !important;
    border-left-color:#000000 !important;
    border-top-color:#000000 !important;
}
}
