css:
@-moz-document url-prefix(http://www.gaiaonline.com/forum),
               url-prefix(http://www.gaiaonline.com/news) {
  /* Widescreen Bubble Fix */
  .speech_bubble.say .top-left {background: url(data:image/png;base64,) top left !important}
  .speech_bubble.say .top-right {background: url(data:image/png;base64,) top right !important}
  .speech_bubble.say .bottom-left {background: url(data:image/png;base64,) bottom left !important}
  .speech_bubble.say .bottom-right {background: url(data:image/png;base64,) bottom right !important}
  .speech_bubble.whisper .top-left {background: url(data:image/png;base64,) top left !important}
  .speech_bubble.whisper .top-right {background: url(data:image/png;base64,) top right !important}
  .speech_bubble.whisper .bottom-left {background: url(data:image/png;base64,) bottom left !important}
  .speech_bubble.whisper .bottom-right {background: url(data:image/png;base64,) bottom right !important}

  /* Ad Column Removal */
  #gaia_content.ss_2Columns_flexiLeft_wideRight #yui-main .yui-b {padding-right: 10px !important;margin-right: 0px !important;}
}
