css:
@-moz-document url(http://www.skritter.com/study/all) 
{	
  #body_wrapper {
    width: 480px;
    height: 832px;
    overflow: hidden; }
  .tabs_div {
    width: 480px;
    padding: 0;
    margin-bottom: 4px; }
  .tab {
    width: 80px; }
  #content_wrapper {
    width: 480px;
    padding: 0;
    position: relative;
    height: 800px;
    box-shadow: none; }
  #skritter_window {
    width: 480px;
    padding: 0;
    height: 758px; }
  div#status_bar {
    height: 40px;
    width: 480px; }
  #item_progress_bars {
    left: 8px;
    top: 8px; }
  #item_progress_bars div.back {
    width: 100px; }
  div#active_lists_button {
    display: none; }
  div#tools_settings_wrapper {
    top: 2px; }
  div#time {
    top: 0;
    left: 280px; }
  #settings_box {
    width: 451px;
    padding: 0 5px;
    top: 38px; }
  #breadcrumbs {
    display: none; }
  #interactive_frame {
    width: 480px;
    position: absolute;
    bottom: 0px;
    height: 555px; }
  #prompt_wrapper, .prompt {
    width: 480px;
    margin-left: 0;
    height: 202px;
    overflow: hidden;
    min-height: 171px; }
  .word_rdng, .word_defn {
    margin-top: 4px; }
  .word_rdng .value {
    font-size: 24px; }
  .char_rdng .value {
    font-size: 20px; }
  #readiness_value {
    display: none; }
  list_sect {
    display: none; }
  #list_sect {
    display: none; }
  #word_mnem, #char_mnem {
    margin-top: 0px;
    margin-bottom: 0px; }
  #word_char_divider {
    margin-bottom: 4px; }
  #char_defn {
    margin-top: 0px;
    margin-bottom: 0px;
    margin-left: 10px;
    display: inline-block; }
  #feedback_tab {
    display: none; }
  .mnem_placeholder {
    height: 14px; }
  #android_reading_error {
    position: absolute;
    width: 300px;
    left: 90px;
    height: 170px;
    top: 0px;
    background-color: white;
    border: 2px ridge black;
    padding: 20px;
    font-size: 18px; }
  #android_reading_error input {
    float: right;
    font-size: 24px; }
  .star_button {
    display: none; }
  div.button .click_area {
    width: 56px;
    height: 56px;
    top: -17px;
    left: -20px; }
  div.prompt div.button {
    margin: 20px; }
  div.prompt div.button.lookup_button {
    margin-top: 17px; }
  body {
    background-image: none; }
  #river {
    display: none; }
  .dict_lookup_box {
    display: none; }
  .popup_close_click_area {
    width: 67px;
    height: 67px;
    margin: -25px 0 0 -25px; }
  #latency_indicator {
    display: none; }
  #logo {
    display: none; }
  #nav_bar_div {
    display: none; }
  #header_block, #header_block.logged_in {
    height: 0; }
  body div.sentence_play_button_play_button.button {
    margin-right: 20px; }
}
