css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(forums.support.roxio.com)
{
#top {display: none  !important; }
#mainNavigation {display: none  !important; }
#master_wrap {width: 90% !important; margin-top: -125px !important; }
.member_wrap {border-color:  #EADFD0 !important; }
.member_wrap h5 {background-color: #AD7034 !important; }
.member_wrap h5 a {color: #FFFFFF  !important; }
#container {margin-top: -50px !important; }
.borderwrap, table, tr, td {border-color:  #EADFD0 !important; }
.postcolor {font-size:95% !important ;}
.maintitle, .maintitlecollapse {background-color: #AD7034 !important; color: #FFFFFF  !important; border-color:  #EADFD0  !important; }
p a {color: #FFFFFF  !important; }
.ipbtable th {background-color: #BD8D5F !important; color: #FFFFFF  !important; }
.ipbtable tbody th {border-color: #EADFD0 !important; }
.ipbtable a {color: #755024 !important ; }
.ipbtable th a {color: #FFFFFF  !important; }
.member_wrap a {color: #755024 !important; }
#_submenu a {color: #755024 !important; }
.subtitle {background: #EADFD0 !important; }
.darkrow1 {background: #EADFD0 !important; }
.formsubtitle {background-color: #FEF8E7 !important; }
.post_head {background: #DFCEB5 !important; }
.popmenubutton-new-out {background-color: #FEF8E7 !important; }
#footerLinks  {display: none  !important; }
.trim  {display: none  !important; }
.codetop,
.sqltop,
.quotetop,
.htmltop {background: #EADFD0 !important; border: 1px dotted #000 !important ; border-bottom: 0 !important; border-left: 4px solid #AD7034 !important; }
.quotemain {border-left: 4px solid #AD7034 !important; color: #755024 !important; }
.codemain,
.sqlmain,
.htmlmain {border: 1px dotted #000 !important ; border-top: 0 !important ; color: #755024 !important; }
.pagelink,
.pagelinklast,
.pagecurrent,
.minipagelink,
.minipagelinklast {background: #EADFD0 !important; color: #755024 !important; }
.pagecurrent {background: #DFCEB5 !important; color: #755024 !important; }
.formbuttonrow, .barc {background-color: #EADFD0 !important; }
.barc a {color: #755024 !important; }
.catend {background-color: #BD8D5F !important; display: none  !important; }
.rte-option-panel  {background-color: #FEF8E7 !important; }
.rte-menu-emo-header  {background-color: #DFCEB5 !important; color: #755024 !important; }
#gfooter {background-color: #EADFD0 !important; color: #755024 !important; }
#gfooter a {color: #755024 !important; }
.copyright  {display: none  !important; }
#fast-reply-controls {background-color: #EADFD0 !important; }
.rte-buttonbar {background-color: #FEF8E7 !important; }
.pp-tabon {background-color: #DFCEB5 !important; color: #755024 !important; }
.pp-taboff {background-color: #AD7034 !important; color: #FFFFFF  !important; }
.pp-tabclear {background-color: #EADFD0 !important; color: #755024 !important; }
.pp-tabon a {background-color: #DFCEB5 !important; color: #755024 !important; }
.pp-taboff a {background-color: #AD7034 !important; color: #FFFFFF  !important; }
.pp-contentbox-back {background-color: #DFCEB5 !important; }
.pp-contentbox-back a {color: #755024 !important; }
#redirectwrap h4 {background-color: #AD7034 !important; color: #FFFFFF  !important; }
.redirectfoot {background-color: #DFCEB5 !important; color: #755024 !important; }
.redirectfoot a {color: #755024 !important; }
a {color: #755024 !important; }
}
