css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("utorrent.com") {

.dda:hover 
{
color:#FFF !important;
background:#00853F !important;
}
#mainwrapper, .infldset, .ipsBox, .table_wrap,
.citation, .ipsBlockquote,
.roundedgreen
{
background:#00853F !important;
}
body, .tcl, .tc2, .tc3, .tcr, #content,
.col_c_icon, .col_c_forum, .col_c_stats, .ipsType_small, .col_c_post,
.ipsSideBlock, .row2, .author_info,
.col_f_icon, .col_f_content, .col_f_views, .col_f_post,
.col_f_preview, .post_wrap, .post_block,
.cleft, .dropdown, #homepagenotice, .block, .inbox, 
.blockpost, h2, .postmsg, .blockform,
#community_app_menu > li.active > a,
.ipsBox_container
{
background:#000000 !important;
}
#ad_space 
{
display:none !important;
}
.postright 
{
background:#000 !important;
border:1px #FFF solid !important;
}
a, h1, h2, h3, h4, h5, h6, ul, li, p, .forumdesc
, .byuser, .stickytext, strong, .rbox, label, .dda, .tc1, 
.tc2, .tc3, .desc, .lighter, .blend_links,
.post, .entry-content 
{
color:#FFF !important;
}
}
