@font-face {font-family: 'Raleway'; src: url('http://themes.googleusercontent.com/static/fonts/raleway/v6/IczWvq5y_Cwwv_rBjOtT0w.woff');}



* {color: #989898 !important; font-family: Raleway, arial !important;}
BODY {background: url('http://colourlovers.com.s3.amazonaws.com/images/patterns/707/707090.png') !important;
background-repeat:repeat !important;
background-position: center top !important;
background-attachment:fixed !important;
margin-bottom: -40px !important;
}




.name, .details {text-transform: capitalize !important;}

a:link, a:active, a:visited {color: #8eb1bd !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}
a:hover {color: #BED2D9 !important; text-decoration:none !important;
-webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}

hr {background: #F2E9C6 !important; }

header {background: -moz-linear-gradient(bottom, #abc5cd 0%, #E4ECED 100%) !important;
background: -webkit-linear-gradient(bottom, #abc5cd 0%, #E4ECED 100%) !important;
background: -o-linear-gradient(bottom, #abc5cd 0%, #E4ECED 100%) !important;
background: linear-gradient(bottom, #abc5cd 0%, #E4ECED 100%) !important;
border-bottom: 3px dashed #F6F6F6 !important;
/*[[Topbar]]*/

section {border-right: 6px solid #E4ECED !important; border-left: 6px solid #E4ECED !important;}
section, #content > div {background: #F6F6F6 !important; }


#tabs li, #content > div > div {background: #F6F6F6 !important; border: 1px solid #E4ECED !important; }
#itemDetails {border: none !important; }

img:hover {opacity:0.6 !important; -webkit-transition: all 0.4s linear;-moz-transition: all 0.4s linear;
transition: all 0.4s linear;}   

.heading td, .grad, #forums td {background:#F6F6F6 !important; }

/* Menu de gauche */
h4 {border: none !important;background: #BED2D9 !important; color:white !important;
padding: 5px !important; text-shadow: 0px 0px 3px white; letter-spacing: 2px;
text-transform: uppercase; font-size: 8px !important; border-radius: 10px 10px 0px 0px !important;}
.toggler {color: white !important; font-size: 8px !important;}

aside > div > ul > li {background: #E4ECED !important;}
aside > div > ul > li:hover {
background: -moz-linear-gradient(left, #FFFFFF, #FFFFFF) repeat scroll 0 0 transparent !important;
background: -webkit-linear-gradient(left, #FFFFFF, #FFFFFF) repeat scroll 0 0 transparent !important;
background: -o-linear-gradient(left, #FFFFFF, #FFFFFF) repeat scroll 0 0 transparent !important;
background: linear-gradient(left, #FFFFFF, #FFFFFF) repeat scroll 0 0 transparent !important;}
aside > div > ul {border: none !important;}


h3, h2 {background: #BED2D9 !important; background-image: url('http://electric.blackness.free.fr/upload/ups/Min%20Jung-5234618H1.jpg') !important; color:white !important; border: none !important; 
padding: 7px 7px 7px 15px !important; text-shadow: 0px 0px 3px white; letter-spacing: 3px;
text-transform: uppercase; font-size: 10px !important; margin-top: 22px !important;}
h3 a:link, h2 a:link, th a:link, h3 a:visited, h2 a:visited, th a:visited {color:white !important; 
font-size: 9px !important; letter-spacing: 1px;}

th {background: #BED2D9 !important; border: none !important;}


input, select, textarea, button, option {background: #E4ECED !important;}


/* Boutons barre du haut */
#realms > li > a > img, .buttons > li > img, .buttons > span > li > a > img {
background: transparent !important; border: none !important; }

#realms img:hover, .buttons img:hover, #realms .active img {box-shadow: 0 1px 5px #bbb inset !important;}

#options {background: #F6F6F6 !important; border: none !important;}

#instant {border: #F6F6F6 !important;}


.spoiler > .content {background: #E4ECED !important; padding:10px!important;}

#compatibility .bar, .bar {
background: -moz-linear-gradient(left top , #F6F6F6, #E4ECED) repeat scroll 0 0 transparent !important;
background: -webkit-linear-gradient(left top , #F6F6F6, #E4ECED) repeat scroll 0 0 transparent !important;
background: -o-linear-gradient(left top , #F6F6F6, #E4ECED) repeat scroll 0 0 transparent !important;
background: linear-gradient(left top , #F6F6F6, #E4ECED) repeat scroll 0 0 transparent !important;}

/* Fond des pourcentages du camembert lol */
.pieLabelBackground, .pieLabel {background: #F6F6F6 !important;}

.synopsis img {box-shadow:none !important; border: none !important;}

.comments > div > div:nth-of-type(odd) {background: transparent !important; border-bottom: 1px solid #CBDBE0 !important;
margin-top: 20px !important;}
.comments td {font-size: 13px !important;}
.comments img {border: 5px solid #CBDBE0 !important; border-radius:10px !important;
margin-right: 10px !important; box-shadow: 0px 0px 3px #F2E9C6 !important;}
.reply-in-place {background:#E4ECED !important; padding: 1px 10px 2px 10px !important;
border: 2px solid #BED2D9 !important; border-radius:10px !important;}


/* Flèches pour parcourir dramas sur le profil */
#navigation-buttons span.active, #navigation-buttons span:hover {
background: none !important;}
.navigation > span {background: #FFF !important;}
.navigation > span:hover, .navigation > div > div:hover {background: #F6F6F6 !important;}

/*couleurs une fois sur deux dans la recherche */
.items > div:nth-child(1), .items > div:nth-child(4), .items > div:nth-child(5), .items > div:nth-child(8), .items > div:nth-child(9) 
{background: none repeat scroll 0 0 #E4ECED !important;}

/*couleurs une fois sur deux dans la liste dramas */
#list > table > tbody > tr:nth-child(even), #stats tr:nth-child(even)
{background: none repeat scroll 0 0 #E4ECED !important;}

/*couleurs une fois sur deux dans la liste des groupes & reviews */
.episodes tr:nth-child(even), .items tr:nth-child(even), .reviews tr:nth-child(even)
{background: none repeat scroll 0 0 #E4ECED !important;}

#listCount {background: #E4ECED !important;}

.mainbox, #roles > div > div, .roles > div > div {background: #E4ECED !important;}
.infobox {background: none repeat scroll 0 0 rgba(228, 236, 237, 0.9) !important; 
padding: 6px !important; margin-top:-4px !important; margin-left: 80px !important;}

section img {
box-shadow: 0px 0px 5px #BED2D9 !important; 
border: 2px solid #BED2D9 !important; border-radius: 20px !important;}

.mainboxfeat {background: none repeat scroll 0 0 rgba(255, 255, 255, 0.8) !important; }

#rating_stats .aggregate table td:nth-of-type(1), #rating_stats .aggregate table td:nth-of-type(2), #rating_stats > div 
{background: #E4ECED !important; border: none !important;}


/*pop-up*/
#dialog .inside > div > .border > .content > .body, #dialog .inside > div > .border > .content > .loading {background: #F6F6F6 !important;
border-color: #CBDBE0 !important; }
#dialog .inside > div > .border {background: none repeat scroll 0 0 rgba(190, 210, 217, 0.7) !important;}
#dialog .inside > div > .border > .content > h1 {background: #CBDBE0 !important; 
text-align:center !important; letter-spacing: 3px; border: #CBDBE0 !important;
text-transform: uppercase; font-size: 11px !important;}
.esc {background: none !important; letter-spacing: 0px; font-size: 9px !important;}

 /*[[Navigation]]*/ 


.help-topic { background: #BED2D9 !important; color:white !important; 
border-bottom: 1px solid white !important; }


