css:
@-moz-document domain("tweakers.net") {
body, table, td, th, input, button, textarea, select, .bar, .sitename, .article h2 {
    font-family: "Segoe UI", Frutiger, "Frutiger Linotype", "Dejavu Sans", "Helvetica Neue", Arial, sans-serif !important;
}

body div.messagecontent, div.article {
    font-size: 1.1em;
}

body #tracker {
    left: -336px;
}

body.hasTracker {
    position: relative;
    margin-left: 0px;
}

body #menubar {
    width: auto;
}

body #tracker .fakeTop>div {
    bottom: auto;
}

body #tracker {
    padding-top: 10px;
}

body #tracker .fakeTop {
    background: #313232;
    background-image: none;
    height: 10px;
}

body div.relevancyColumn {
    float: right;
    width: 200px;
    margin-left: 0px;
    font-size: 0.8em;
    line-height: 0.8em;
}

body div.relationboxBlock {
    margin-bottom: 0px;
}

body div#relationbox, div#rbEntitylist, #rbEntitylist ul {
    margin: 0px;
    border: 0;
}

body .relevancyColumn h2 {
    font-size: 1em;
    margin: 5px 0 5px;
}

body .articleColumn {
    width: 60em;
}

body #reacties {
    width: 60em;
}

body .newsArticleColumn .nextPrevDay {
    max-width: none;
}

body #collapseButtons, #collapseButtons .toggleThread {
    visibility: visible !important;
}

body .reactieHeader {
    padding-left: 40px;
}

body #collapseButtons {
    position: absolute;
    top: 5px;
    left: 5px;
    width: 40px;
    height: 20px;
}

body .usericonsmall {
    display: none;
}

body #reacties #reactieContainer {
    border: 0;
    padding: 0;
}

body .reactie {
    margin-left: 5px;
    margin-right: 5px;
    margin-top: 0;
    margin-bottom: 8px;
    background-color: #ffffff;
    border: 1px solid #e6e6e6;
    padding: 5px 8px 0 5px;
}

body .reactie,
body .reactie .reactie .reactie,
body .reactie .reactie .reactie .reactie .reactie,
body .reactie .reactie .reactie .reactie .reactie .reactie .reactie,
body .reactie .reactie .reactie .reactie .reactie .reactie .reactie .reactie .reactie {
    background-color: #fff;
}

body .reactie .reactie,
body .reactie .reactie .reactie .reactie,
body .reactie .reactie .reactie .reactie .reactie .reactie,
body .reactie .reactie .reactie .reactie .reactie .reactie .reactie .reactie,
body .reactie .reactie .reactie .reactie .reactie .reactie .reactie .reactie .reactie .reactie {
    background-color: #F7F7F8;
}

body .reactieBody {
    border: 0;
}

body table.listing td {
    border-bottom: 1px solid #E2E2E2;
    border-top: 0px solid #F9FCFC;
    background: #EDEEEE;
    background-image: none;
}

body table.listing td.ind, table.listing td.ind2 {
    border-left: 0;
}

body table.listing td.last {
    border-right: 0;
}

body div.message {
    background-image: url(http://tweakers.net/ext/f/hD9Vqt8SK2Yk6hoDO80ET50J/full.png);
    background-repeat: repeat-x;
    border: 0;
  padding-top: 7px;
  margin-bottom: 0px;
  -webkit-box-shadow: none;
  box-shadow: none;
}

body div.message .post {
    margin-top: 39px;
  
}

body div.message .poster>.username {
    margin-bottom: 12px;
}
}
