css:
@-moz-document regexp('(.*10\.3\.161.*)')

{

td {
    border-color: #101010 !important;
}
td#report_title h1 {
    color: #FFFF00 !important;
}
table a {
    color: #C0C0C0 !important;
}
input[type=text] {
    background-color: #202020 !important;
    opacity: .6 !important;
    color: #FFF !important;
}
select,textarea {
    opacity: .8 !important;
    background-color: #B0B0B0 !important;
  
}
input.button-no-changes {
    background-color: #202020 !important;
    background-image: none !important;
}
input.button-secondary {
    font-size: 12px !important;
    color: #D0D0D0 !important;
    height: 22px !important;
    text-align: center !important;
    background-image: -moz-linear-gradient(top, #580000 6%, #101010 80%) !important;
    border-color: #505050 !important;
    border-radius: 15px !important;
    padding-right: 4px !important;
    padding-left: 4px !important;
    padding-bottom: 4px !important;
    padding-top:0px !important;
    text-decoration: none !important;
    box-shadow: 3px 3px 3px 1px #000;
    margin-bottom: 5px !important;
    margin-right: 5px !important;
    margin-top: 5px !important;
    border-style: none !important;
}
button[type=button] {
    font-size: 13px !important;
    color: #D0D0D0 !important;
    text-align: center !important;
    background-image: -moz-linear-gradient(top, #580000 6%, #101010 80%) !important;
    border-color: #505050 !important;
    border-radius: 15px !important;
    padding-right: 4px !important;
    padding-left: 4px !important;
    padding-bottom: 4px !important;
    padding-top:0px !important;
    text-decoration: none !important;
    box-shadow: 3px 3px 3px 1px #000;
    margin-bottom: 5px !important;
    margin-right: 5px !important;
    margin-top: 5px !important;
    border-style: none !important;
}
input.submit,.cancel {
    font-size: 14px !important;
    color: #FFFF00 !important;
    height: 30px !important;
    text-align: center !important;
    background-image: -moz-linear-gradient(top, #000 10%, #101010 90%) !important;
    border-color: #505050 !important;
    border-radius: 10px !important;
    padding-right: 8px !important;
    padding-left: 8px !important;
    padding-bottom: 5px !important;
    padding-top:0px !important;
    text-decoration: none !important;
    box-shadow: 3px 2px 4px 2px #FFFF00;
    margin-bottom: 5px !important;
    margin-right: 5px !important;
    margin-top: 5px !important;
    border-style: none !important;
}
#header {
    background-color: #202020 !important;
    color: #B0B0B0 !important;
}
img.logo {
    background-image: -moz-linear-gradient(top, #580000 6%, #101010 80%) !important;
    border-color: #505050 !important;
    border-radius: 15px !important;
    padding-right: 8px !important;
    padding-left: 8px !important;
    padding-bottom: 5px !important;
    padding-top:0px !important;
    text-decoration: none !important;
    box-shadow: 3px 3px 3px 1px #000;
    margin-top: 10px !important;
    margin-bottom: 10px !important;
}
#header-helpbar {
    background-color: #202020 !important;
    color: #B0B0B0 !important;
}
body {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
}
div {
    border-top-color: #101010 !important;
}
#outer-content {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
}
h1#page_title {
    color: #B0B0B0 !important;
}
#content {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
}
#nav_menu * {
    background-color: #202020 !important;
    color: #FFFF00 !important;
    background-image: none !important;
    border-color: #101010 !important;
    font-size: 14px !important;
}
/* Login */
dl.box dt {
    background-color: #580000 !important;
    color: #C0C0C0 !important;
    border-color: #580000 !important;
}
dl.box dd {
    background-color: #101010 !important;
    color: #C0C0C0 !important;
    border-color: #580000 !important;
}
table.pairs th,label {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
    border-color: #101010 !important;
}
input[name=username] {
    background-color: #202020 !important;
    color: #000 !important;
    border-color: #202020 !important;
    background-image: none !important;
    border-style: none !important;
    opacity: .4;
}
input[name=password] {
    color: #000 !important;
    border-color: #202020 !important;
    opacity: .4;
}
.action-bar {
    background-color: #101010 !important;
    background-image: none !important;
    color: #B0B0B0 !important;
}
input.button {
    font-size: 14px !important;
    color: #D0D0D0 !important;
    height: 22px !important;
    text-align: center !important;
    background-image: -moz-linear-gradient(top, #580000 6%, #101010 80%) !important;
    border-color: #505050 !important;
    border-radius: 15px !important;
    padding-right: 8px !important;
    padding-left: 8px !important;
    padding-bottom: 5px !important;
    padding-top:0px !important;
    text-decoration: none !important;
    box-shadow: 3px 3px 3px 1px #000;
    margin-bottom: 5px !important;
    margin-right: 5px !important;
    margin-top: 5px !important;
    border-style: none !important;
}

/* NavBar */

#yui-navbar * {
    color: #B0B0B0 !important;
}
#yui-navbar a:hover {
    color: #000 !important;
    background-color: #B0B0B0 !important;
}
#yui-navbar a.yuimenubaritemlabel-selected {
    color: #000 !important;
    background-color: #B0B0B0 !important;
}
#yui-navbar-container {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
    border-color: #101010 !important;
}
#yui-navbar li.other {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
    border-color: #780000 !important;
}
#yui-navbar .current {
    background-color: #780000 !important;
    color: #B0B0B0 !important;
    border-color: #101010 !important;
}
#yui-navbar .visible {
    background-color: #101010 !important;
    color: #C0C0C0 !important;
    border-color: #B0B0B0 !important;
}
/* Content */
#content table tr.default td a {
    color: #FFFF00 !important;
}
#content table tr.default td,th {
    background-color: #202020 !important;
    border-color: #101010 !important;
    color: #B0B0B0 !important;
}
#content table td.default {
    background-color: #202020 !important;
    border-color: #101010 !important;
    color: #B0B0B0 !important;
}
#content table .not_in_use {
    background-color: #202020 !important;
    color: #B0B0B0 !important;
}
tr {
    border-color: #202020 !important;
}

/* Content Filters */

form#filter_form dd .text-info {
    background-color: #202020 !important;
    color: #B0B0B0 !important;
}

/* Add Filter */

#rule_dialog {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
}
#rule_dialog .bd {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
}
#rule_dialog .bd #condition_rule_panel {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
}
#condition_rule_panel .yui-nav li * {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
}
#condition_rule_panel li[title=active] * {
    background-color: #B0B0B0 !important;
    color: #000 !important;
}
#condition_rule_panel .yui-content {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
}
#condition_rule_panel h3.rule_name {
    color: #FFFF00 !important;
}

/* user Roles */
td.disabled-group {
    background-color: #101010 !important;
}
/* Feature Keys */
form[name=activate_list_form] .text-info,.group {
    background-color: #202020 !important;
    color: #FFFF00 !important;
    border-color: #101010 !important;
}

form[action$=overview] th.col-cell img {
    height: 15px !important;
    text-align: center !important;
    background-image: -moz-linear-gradient(top, #101010 10%, #A0A0A0 90%) !important;
    border-color: #505050 !important;
    border-radius: 2px !important;
    padding-right: 4px !important;
    padding-left: 2px !important;
    padding-bottom: 2px !important;
    padding-top:0px !important;
    text-decoration: none !important;
    box-shadow: 1px 1px 1px 1px #000;
    margin-bottom: 2px !important;
    margin-right: 2px !important;
    margin-top: 2px !important;
    border-style: none !important;
}

/* Overview */
form#report_form dl.header dt  {
    background-color: #202020 !important;
    color: #B0B0B0 !important;
    border-color: #580000 !important;
}
form#report_form dl.header dd * {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
    border-color: #101010 !important;
}
form#report_form dl.datebox dd {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
    border-color: #580000 !important;
}
form#report_form dl.datebox table.date * {
    background-color: #202020 !important;
    color: #B0B0B0 !important;
    border-color: #580000 !important;

}
form#report_form table.report_section * {
    background-color: #202020 !important;
    color: #B0B0B0 !important;
    border-color: #580000 !important;

}
form#report_form table.report_section * {
    background-color: #202020 !important;
    color: #B0B0B0 !important;
    border-color: #580000 !important;

}
form#report_form table.report_section#s_0 * {
    background-color: #101010 !important;
    color: #B0B0B0 !important;
    border-color: #580000 !important;

}




}
