css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cvs.php.net"),
               domain("cvs.sourceforge.net"),
               domain("svn.apache.org"),
               domain("svn.sourceforge.net")
{

 a,
 a:link,
 a:link:visited,
 .vc_blame_rev
  {
    color: #E8E1BD !important;
  }
 a:link:hover
  {
    color: #fff !important;
  }

 html,
 body
  {
    background: #222 !important;
    border-color: #000 !important;
    color: #ccc !important;
  }
#vc_main_body {
    background: none repeat scroll 0% 0% #222 !important;
}
.vc_log {
    background-color: #222 !important;
}
.vc_log_block_even {
    background-color: #333 !important;
}
.vc_log_block_odd {
    background-color: #444 !important;
}

.vc_file_line_text {
    background-color: #222 !important;
}
table.vc_idiff tbody th ,
.vc_file_line_number {
    background: #000 !important;
    color: #b97 !important;
}

 .vc_blame_text
  {
    background: none !important;
  }

 tr.vc_diff_header > th:last-child,
 .vc_idiff_add:last-child,
 .vc_idiff_change:last-child,
 .vc_idiff_chunk_header > td:last-child,
 .vc_idiff_nochange:last-child,
 .vc_idiff_remove:last-child,
 .vc_diff_add:last-child,
 .vc_diff_change:last-child,
 .vc_diff_chunk_header > td:last-child,
 .vc_diff_nochange:last-child,
 .vc_diff_remove:last-child,
 .vc_blame_line
  {
    border-left: 1px solid #000 !important;
  }
 .vc_blame_text
  {
    border-right: 1px solid #000 !important;
  }
 .vc_blame_author,
 .vc_blame_line
  {
    border-right: 1px solid #222 !important;
  }
 .vc_diff_chunk_header > td:last-child
  {
    padding-left: 1em !important;
  }
 tr:last-child > td[class^="vc_diff_"],
 tr:last-child > td[id^="l"]
  {
    border-bottom: 1px solid #000 !important;
    padding-bottom: 1em !important;
  }
 tr:first-child td[class^="vc_blame_"]
  {
    border-top: 1px solid #000 !important;
  }
 tr:last-child td[class^="vc_blame_"]
  {
    border-bottom: 1px solid #000 !important;
  }

 tr:not(:last-child) td[class^="vc_blame_"]
  {
    border-bottom: 1px dotted #222 !important;
  }

 .vc_blame_author
  {
    color: #ddd !important;
  }

 .vc_blame_line
  {
    color: #aaa !important;
  }

 .vc_blame_author,
 .vc_blame_line,
 .vc_blame_text
  {
    padding: 0 .25em !important;
  }

 .vc_idiff_change_empty,
 .vc_idiff_empty,
 .vc_diff_change_empty,
 .vc_diff_empty
  {
    background: #111 !important;
    border: 1px dashed #666 !important;
  }

 .vc_summary
  {
    background: #000 !important;
    border: 2px dotted #ccc !important;
    color: #b97 !important;
    padding: .5em !important;
  }

 .vc_row_odd
  {
    background: #333 !important;
    border-color: #000 !important;
    color: #bbb !important;
  }

 .vc_row_even
  {
    background: #555 !important;
    border-color: #000 !important;
    color: #ccc !important;
  }

 /* grey poupon */
 .vc_diff_header,
 .vc_header
  {
    background: #69675C !important;
    border-color: #000 !important;
    color: #bbb !important;
  }

 /* lighter grey poupon */
 .vc_diff_chunk_header,
 .vc_header_sort
  {
    background: #918E80 !important;
    color: #000 !important;
  }

 /* green bg */
 .vc_idiff_add,
 .vc_diff_add
  {
    background-color: #3D5045 !important;
    border-color: #000 !important;
  }

 /* cerulean bg */
 .vc_idiff_change,
 .vc_diff_change
  {
    background-color: #334244 !important;
    border-color: #000 !important;
  }
 .vc_idiff_changes2,
 .vc_diff_changes2
  {
    background-color: #808000 !important;
    border-color: #000 !important;
  }

 /* red bg */
 .vc_idiff_remove,
 .vc_diff_remove
  {
    background: #913233 !important;
    color: #ccc !important;
  }
.pygments-nv {
    color: rgb(0, 100, 255) !important;
}

}
