css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("api.jquery.com") {
    body p {
        font-size: 13px;
    }

    h1, h2, h3, h4, h5, h6 {
        font-family: 'helvetica', 'arial', sans-serif !important;
    }
    
    .jquery h2.logo a, .jquery-learn h2.logo a {
        background-size: 100% auto !important;
        width: 200px !important;
    }
    
    #container {
        background-color: #222 !important;
    }
    
    #global-nav nav {
        height: 37px !important;
    }
    
    nav#main{
        box-shadow: 0 0 0 transparent !important;
    }
    
    nav#main li {
        font-family: 'helvetica', 'arial', sans-serif !important;
        
    }
    
    nav#main li a:hover, nav#main li.current a, nav#main .searchform {
        background: rgba(255, 255, 255, .1);
    }
    
    nav#main .searchform input:-moz-placeholder {
        color: rgba(255, 255, 255, 0.3) !important;
    }
    
    .constrain, #content-wrapper {
        max-width: 1000px !important;
    }
    
    #content-wrapper {
        padding: 20px 40px 0 !important;
        overflow: hidden !important;
        background: #ccc !important;
    }
    
    .entry-wrapper {
        border: 1px solid #999 !important;
    }
    
    .demo, .code-demo {
        background: #eee !important;
        padding: 10px !important;
        border: 1px solid #999 !important;
        margin-bottom: 20px !important;
    }
    
    .demo iframe {
        border: 0 !important;
    }
    
    #sidebar {
        background: #ddd !important;
        border: 0 !important;
    }
    
    .syntaxhighlighter {
        font-size: 12px !important;
        margin: 1.6em 0 !important;
        background: #003947 !important;
    }
    
    #content-wrapper {
        max-width: 1000px;
    }
    
    pre code {
        color: #cecece !important;
    }
    
    .string {
        color: #00b1ac !important;
    }
    
    .keyword {
        color: #e0540d !important;
    }
    
    .gutter .line {
        color: #065c6d !important;
        padding-bottom: 1px !important;
    }
    
    .regexp {
        color: #eb0012 !important;
    }
    
    .tag, .title {
        color: #009cde !important;
    }
    
    .attribute {
        color: #cecece !important;
    }
    
    .value {
        color: #00b4ac !important;
    }
    
    .number {
        color: #e7288f !important;
    }
    
    .doctype {
        font-weight: normal !important;
    }
    
    .cat-item {
        background-color: #cecece !important; 
        background-image: none !important;
    }
    
    #categories > ul > .cat-item {
        border-bottom: 1px solid #c7c7c7;
        border-left: 2px solid #999 !important;
        border-right: 1px solid #ccc !important;
    }

    #categories > ul > .cat-item a {
        padding-top: 5px;
        padding-bottom: 5px;
    }
    
    .cat-item > ul {
        border-top: 1px solid #c7c7c7;
        border-bottom: 1px solid #c7c7c7;
    }
        
    .children .cat-item {
        background-color: #ddd !important;
        border-bottom: 1px solid #cecece;
        padding: 0px !important;
        border-left: 2px solid #999 !important;
    }
    
    #sidebar li a {
        line-height: 120%;
    }
    
    #sidebar li .children a {
        padding-left: 20px !important;
    }
    
    #sidebar li .children a:hover {
        background-color: #CEDCEA;    
    }
    
    .listing .hentry {
        margin: 0 !important;
        padding: 20px !important;
        background-color: rgba(255, 255, 255, .3) !important;
        border-radius: 0 !important;
        border: 0 !important;
        border-bottom: 1px solid #cecece !important;
    }
    
    hr {
        height: 0 !important;
        border: 0 !important;
        border-bottom: 1px solid #eee !important;
        display: none;
    }
    
    #content .section-title {
        border-radius: 0 !important;
    }
    
    .signatures li h4 {
        background: #555 !important;
        font-weight: normal !important;
        padding-top: 10px !important;
    }
    
    #content blockquote p {
        font: normal 14px/24px 'helvetica', 'arial', sans-serif !important;
    }
    
    .longdesc {
        margin-bottom: 40px !important;
    }
    
    .longdesc ul li, .toc ul li {
        background: none !important;
        list-style-type: disc !important;
    }
    
    .desc {
        font-weight: normal;
        font-style: normal !important;
    }
    
    strong {
        display: block;
        padding-bottom: 10px;
    }
    
    pre .container .line {
        padding-bottom: 1px !important;
    }
    
    #container, footer {
        border: 0 !important;
    }
}
