css:
/*去掉字体限制*/
body, html {
    /**/
	font-size:100%
}
div.right {
    font-size:110% !important;
}

/*图片显示看的更爽*/
.postImage {
	height:auto !important;
	max-height:280px !important;
}
.postImage img {
	max-height: 1000% !important;
/*	max-width: 100% !important;*/
}
/*左栏列表美化*/
.subscriptions li {
	font-weight: normal;
	padding:5px 10px;
}
.subscriptions li.selected, .noTouch .subscriptions li.selected, .subscriptions li.selected .buttonChar {
	background-color: #FFFFC0;
}
/*卡片视图 标题换行*/
.msgBody a{
    white-space:normal !important;
    font-size:100% !important;
}
/*
.subscriptionItem, .mosaic .msubject, .fromFolder, .foldersMenu li, .tagsMenu li, .ui-menu-item a, .mline, .fromFolderLV{
    white-space:normal !important;
}
*/
/*未读条目为红色标题*/
div[class^=expandable] .msubject{
    color:#ff6699 !important;
}
/*折腾图标*/
.msgFrame .symbolButton
{
    display:block !important;
    width: 32px;
    height: 32px;
    max-width: 32px !important;
    padding-left: 0 !important;
    padding-right: 0 !important;
    padding-top: 0.3em;
    padding-bottom: 0.3em;
}

.msgButtons
{
    text-align:left !important;
}

.msgButtons .symbolButton, .msgButtons .mtime
{
    padding:0 !important;
}

.msgButtons .mtime
{
    padding: 0 0 !important;
}
.btnTag
{
    background-position: 0 0 !important;
    background-image: url("http://download.easyicon.net/png/523440/32/") !important;
    height: 32px !important;
    width: 32px !important;
}
/*分享图标*/
.btnShare
{
    background-position: 0 0 !important;
    background-image: url("http://download.easyicon.net/png/523505/32/") !important;
    height: 32px !important;
    width: 32px !important;
}
/*收藏图标*/
.btnStar
{
    background-position: 0 0 !important;
    background-image: url("http://download.easyicon.net/png/523446/32/") !important;
    height: 32px !important;
    width: 32px !important;
    opacity: 0.25 !important;
}
.btnStar:hover
{
    opacity: 1 !important;
}
/*收藏图标选择态*/
.starred .btnStar
{
    background-position: 0 0 !important;
    background-image: url("data:image/png;base64,") !important;
    height: 32px !important;
    width: 32px !important;
    opacity: 1 !important;
}
/*标签菜单*/
.tagsMenu
{
    margin-left:-15.2em !important;
    margin-top: 0 !important;
    width: 15em !important;
}
