css:
@-moz-document url-prefix("about:newtab"){
.newtab-title{
font-size:420% !important;
line-height:320% !important;/*根据屏幕大小自行调整*/
transition: border-top 0.27s, line-height 0.21s, width 0.15s, height 0.15s, font-size 0.24s;
}

.newtab-title:hover{
border-top: solid 20px black !important;
font-size:32px !important;
line-height:180px !important;
width:110%;/*根据九宫格的数目大小自行调整*/
height:130%;/*根据九宫格的数目大小自行调整*/
z-index:9999;
box-shadow: 0px 0px 12px 0px hsl(70,6%,56%) !important;
}

.newtab-title:hover{
background:url("data:image/gif;base64,") no-repeat 6% 105%  !important;
}


.newtab-title:active{
cursor:wait !important;
font-size:420% !important;
line-height:320% !important;/*根据九宫格的数目大小自行调整*/
width:94%;/*根据九宫格的数目大小自行调整*/
height:89%;/*根据九宫格的数目大小自行调整*/
}


.newtab-row:nth-child(2)>.newtab-cell:nth-child(2n)>.newtab-site>.newtab-link>.newtab-title{
background-color:hsl(10,90%,75%) !important;
color:hsl(10,75%,17%) !important;}
.newtab-row:nth-child(1)>.newtab-cell:nth-child(2n+1)>.newtab-site>.newtab-link>.newtab-title{
background-color:hsl(40,80%,85%) !important;
color:hsl(40,75%,17%) !important;}
.newtab-row:nth-child(2)>.newtab-cell:nth-child(2n+1)>.newtab-site>.newtab-link>.newtab-title{
background-color:hsl(60,80%,75%) !important;
color:hsl(60,75%,17%) !important;}
.newtab-row:nth-child(3)>.newtab-cell:nth-child(2n)>.newtab-site>.newtab-link>.newtab-title{
background-color:hsl(120,80%,78%) !important;
color:hsl(120,75%,17%) !important;}
.newtab-row:nth-child(1)>.newtab-cell:nth-child(2)>.newtab-site>.newtab-link>.newtab-title,
.newtab-row:nth-child(3)>.newtab-cell:nth-child(3)>.newtab-site>.newtab-link>.newtab-title{
background-color:hsl(205,90%,70%) !important;
color:hsl(205,75%,17%) !important;}
.newtab-row:nth-child(3)>.newtab-cell:nth-child(1)>.newtab-site>.newtab-link>.newtab-title,
.newtab-row:nth-child(1)>.newtab-cell:nth-child(4)>.newtab-site>.newtab-link>.newtab-title{
background-color:hsl(255,90%,80%) !important;
color:hsl(255,75%,17%) !important;}

}
