css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.buff-community.de/users/index"), url("http://www.buff-community.com/users/index") {
    #wrapper{
        margin:10px 2px !important;
    }
    .bc-logo{
        display:none !important;
    }
    .fb-area{
        display:none !important;
    }
    .user-right{
        margin-left:2px !important;
    }
    #friend-area{
        float:right !important;
        width:181px !important;
    }
    div[class*=" finish-tm"]{
        position:absolute !important;
        top: 36px !important;
        margin:0px 0px 0px 30px !important;
        padding:0px 0px 5px 0px !important;
    }
    #accepted-buffs-data div.title-column[style^="width:280px;"]{
        margin-left:110px !important;
    }
    #accepted-buffs-data div.title-column[style="width:80px"]{
        position:absolute !important;
        margin-left:30px !important;
    }
    div[id^="accepted-buffno-"]{
        position:relative !important;
        height:72px !important;
    }
    div[id^="accepted-buffno-"] div[style*=" color: red;"]{
        display:none !important;
    }
    div[id^="accepted-buffno-"] div:nth-child(2){
        margin-left:80px !important;
    }
    div[id^="accepted-buffno-"] div:nth-child(6){
        position:absolute !important;
        margin-left:30px !important;
    }
    button[id^="finish-btn-"]{
        position:absolute !important;
    }
    button[id^="finish-btn-"][id$="-5"]{
        left:310px !important;
    }
    button[id^="finish-btn-"][id$="-4"]{
        left:400px !important;
    }
    button[id^="finish-btn-"][id$="-3"]{
        left:0px !important;
    }
    button[id^="finish-btn-"][id$="-2"]{
        left:90px !important;
    }
    button[id^="finish-btn-"][id$="-1"]{
        left:180px !important;
    }
    button.buffed-pruduct-disabled{
        display:none !important;
    }
    form[id^="buff-finish-"]{
        width:650px !important;
        height:25px !important;
    }
    form[id^="buff-finish-"] span{
        display:none !important;
    }

}
