css:
span.newtab-title {
    background: inherit !important;
    font-family: FreeSans, Arial;  
    font-weight: bold;
    font-size: 20px;
    text-align: center;
    top: 50%;
    color: #000000 !important;
    text-shadow: 0px 0px 3px grey;
}
div.newtab-cell {
    transition: 0.4s all ease-in-out !important;
}
div.newtab-cell:hover {
    border-left: 25px solid #686868 !important;
    box-shadow: 1px 1px 20px #686868;
}
