css:
@-moz-document domain('neopets.com'){



#main #header {border: none !important; background-image: url(http://img824.imageshack.us/img824/8145/jbneopets2.png) !important;}

#main #ban {border: none !important; background-image: url(http://imageshack.us/a/img12/5058/jbneopets.png) !important;}

#main #footer {background-image: url(http://img809.imageshack.us/img809/8643/jbneopetsfooter.png) !important;}

#main #footer td {text-align: center !important;}

.footerNifty {display: none !important;}

.copyright .sf {position: relative !important; left: 100px !important;}

.copyright .sf img {display: none !important;}

a:link, a:visited, a:active {color: #006CB0;}

a:hover {color: #5290B1;}

.eventIcon, #header td.user.medText {color: #000000 !important;}

.eventIcon a:link, #header td.user.medText a:link {color: #51321E !important;}

.eventIcon a:visited, #header td.user.medText a:visited {color: #988A9F !important;}

.eventIcon a:active, #header td.user.medText a:active {color: #988A9F !important;}

.eventIcon a:hover, #header td.user.medText a:hover {color: #CF90A7 !important;}

#nst {color: #000000 !important;}

.eventIcon {color: #000000;}

ul.dropdown {background: #FFFFFF !important; border: none !important; opacity: 0.9 !important; filter: alpha(opacity=90) !important;}

li:hover ul.dropdown a, li.over ul.dropdown a {color: #000000 !important;}

li:hover ul.dropdown a:Hover, li.over ul.dropdown a:hover {color: #666666 !important;}

.marqueeSlot, .marqueeSlot a:link, .marqueeSlot a:visited, .marqueeSlot a:active, .marqueeSlot a:hover {background-color: #C7B4D0 !important;}

.marqueeSlotOn, .marqueeSlotOn a:link, .marqueeSlotOn a:visited, .marqueeSlotOn a:active, .marqueeSlotOn a:hover {background-color: #592780 !important;}

.sidebarHeader a:link, .contentModuleHeader a:link, .contentModuleHeaderAlt a:link {color: #FFFFFF !important;}

.sidebarHeader a:visited, .contentModuleHeader a:visited, .contentModuleHeaderAlt a:visited {color: #FFFFFF !important;}

.sidebarHeader a:active, .contentModuleHeader a:active, .contentModuleHeaderAlt a:active {color: #FFFFFF !important;}

.sidebarHeader a:hover, .contentModuleHeader a:hover, .contentModuleHeaderAlt a:hover {color: #FFFFFF !important;}

.moreLink, .moreLinkAlt {border: #FFFFFF 1px solid !important;}

.sidebarHeader {background: #3D3053 !important; color: #FFFFFF !important; border: none !important;}

.contentModuleHeader, .contentModuleHeaderAlt {background: #592780 !important; color: #FFFFFF !important; border: none !important;}

.sidebarModule, .contentModule {border: none !important;}

.sidebarTable {border: #3D3053 2px solid !important;}

.contentModuleTable {border: #592780 2px solid !important;}

#main #footer div[style="position:absolute;right:257px;top:52px;overflow:hidden;"] {display: none !important;}

#main #footer a img[width="156"][height="46"] {display: none !important;}

  

  
}
