css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://www.tumblr.com/"),
               url-prefix("https://www.tumblr.com/"),
               url-prefix("http://tumblr.com/"),   
               url-prefix("https://tumblr.com/"),
               regexp('(http|https)://www\\.tumblr\\.com/(?!dashboard/iframe).*'),
               regexp('(http|https)://www\\.tumblr\\.com/(?!customize).*') {

body {
	background:url('http://static.tumblr.com/x2hfity/63vneqcvx/bg-caskett.png') left bottom #5e5e61 no-repeat fixed!important;
}

.inbox_notice_container,
.new_post_notice_container {border: 2px solid #353536}


.plus-follow-button {color: #999;}

.plus-follow-button:hover {background: #808080}


.compose-button {
	color: #5e5e61;
	border-color: #808080;
	background: #808080;
}

.compose-button:active {
	background-color: #002196;
	border-color: #002196;
	color: rgba(94,94,97,.8)
}

.compose-button[disabled],
.compose-button.disable,
.compose-button[disabled]:active,
.compose-button.disabled.active {
	background-color: #808080;
	border-color: #808080;
	color: rgba(94,94,97,.5)
}


.l-header-container--refresh {background-color: #353536}

.l-header-container--refresh .tab_notice {color: #e1e1e1}


.logged-out-header .login-button {color: #5e5e61}

.logged-out-header .login-button:active {color: rgba(94,94,97,.8)}

.logged-out-header .login-button[disabled],
.logged-out-header .login-button.disable,
.logged-out-header .login-button[disabled]:active,
.logged-out-header .login-button.disabled.active {color: rgba(94,94,97,.5)}

.logged-out-header .signup-button {
	border-color: #808080;
	background: #808080;
}

.logged-out-header .signup-button:active {
	background-color: #002196;
	border-color: #002196;
}
.logged-out-header .signup-button[disabled],
.logged-out-header .signup-button.disable,
.logged-out-header .signup-button[disabled]:active,
.logged-out-header .signup-button.disabled.active {
	background-color: #808080;
	border-color: #808080;
}


.plexi.show.color,
.glass.blue,
#hello_glass.blue,
#glass_overlay.blue {background: rgba(94,94,97,.95)}


.flat-button.blue {
	color: #FFF;
	border-color: #808080;
	background: #808080;
}

.flat-button.blue:active {
	background-color: #002196;
	border-color: #002196;
}

.flat-button.blue[disabled],
.flat-button.blue.disable,
.flat-button.blue[disabled]:active,
.flat-button.blue.disabled.active {
	background-color: #808080;
	border-color: #808080;
}


.indash_header_wrapper .editable_text {border-color: #808080}

.indash_header_wrapper .avatar .popover .avatar_shape.selected {color:#808080}


.ui_dialog_lock.opaque {background: rgba(94,94,97,.95)}

.ui_dialog .chrome.blue {
	background-color: #808080;
	border-color: #808080;
}


.nncampaign-page .binary_switch input[type="checkbox"]:checked~.binary_switch_track {background: #808080}

.binary-switch input[type="checkbox"]:checked~.binary-switch-track {background: #808080}


.post-forms-glass {background: rgba(94,94,97,.95)}

.post-form .chrome.blue,
.pop-menu .chrome.blue {
	border-color: #808080;
	background:#808080;
}

.overlay-button.active .icon {color: #808080}
 
.editor .over-top::before,
.editor .over-bottom::after {background: #808080}


.identity {background: #5e5e61}
 
.identity-legacy {background: #5e5e61}

.identity-legacy .controls_section .item:hover .icon_plus,
.identity-legacy .controls_section.user_menu .item:hover {background-color: #101010!important}

.identity-legacy .controls_section.user_list li .follow_list_item_blog:before {
	background: #1f1f1f;
	border-right: 0;
 }

.identity-legacy .controls_section.user_list li:hover .follow_list_item_blog:before,
.identity-legacy .control_hover .controls_section.user_list li:active .follow_list_item_blog:before {
	background: #1f1f1f;
	border-right: 0;
 }

.identity-legacy .controls_section.user_list li:active .follow_list_item_blog:before {
	background: #1f1f1f;
	border-right: 0;
 }

.identity-refresh {background-color: #5e5e61}

.identity-refresh .l-container.l-container--two-column .l-content,.identity-refresh .l-container.l-container--two-column-dashboard .l-content {
	width: 97%;
	margin: -50px 0 0 -10px;
	background-color: #353536;
	padding: 40px 20px 0!important;
}

.identity-refresh .l-container.l-container--two-column .right_column,.identity-refresh .l-container.l-container--two-column-dashboard .right_column {
	background-color: #5e5e61;
	width:229px;
}

.identity-refresh .l-container.l-container--two-column-dashboard .left_column {background-color: #5e5e61}
 
.identity-refresh .l-container.l-container--flex .l-content {background-color: #5e5e61}

.identity-refresh .tab_notice .tab_notice_value {color: #e1e1e1}

.identity-refresh .post_avatar {background-color: #5e5e61}

.identity-refresh .post_avatar.flat {border: 1px solid #5e5e61}

.identity-refresh .post_avatar .post_avatar_link {background: #5e5e61 50% 50% no-repeat}
 
.post_avatar.lighter_blue {background-color: #5e5e61}

.identity-refresh .post_full .post_permalink {border-color: #5e5e61}

.identity-refresh .right_column:after {background: none}

.identity-refresh .send_fan_mail {background: #808080}

.identity-legacy .send_fan_mail {background: #808080}

.identity-refresh .tab_notice,
.identity-refresh .selected .tab_notice {background: #b94242}

.identity-refresh .controls_section .item:hover .follow:hover {background: #808080}

.identity-refresh .controls_section.user_list li:hover .follow_list_item_blog {
	background-color: #212121!important;
	background-image: none!important;
	border-right: 0!important;
}

.identity-refresh .controls_section.user_list li .follow_list_item_blog:before {
	background-image: none!important;
	border-right: 0!important;
}

.identity-refresh .controls_section.user_list li:hover .follow_list_item_blog:before,
.identity-refresh .controls_section.user_list li:active .follow_list_item_blog:before {
	background-image: none!important;
	border-right: 0!important;
}

.controls_section_item:hover,
.controls_item_anchor:hover,
.controls_item_account:hover {background-color: #212121!important}

.group_label,
.group_content {color:#fff}

.checkbox .setting,
.text .setting,
.accordion_trigger,
.flat_select {color:#e1e1e1}

.settings_heading,
.blog_info {color:#e1e1e1!important}

.form_wrapper {background: #353536}

.post_form_wrapper_inner .form_wrapper {background: #fff}


#popover_blogs .tab_notice:after {
	display: none;
}

div.l-content.l-content.l-content {
	background-color: rgba(196,198,198,0.3);
	-moz-border-radius: 12px!important;
	-webkit-border-radius: 12px!important;
	-o-border-radius: 12px!important;
	-ms-border-radius: 12px!important;
	border-radius: 12px!important;
	padding-top: 25px!important;
	padding-bottom: 25px!important;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {
	background: none!important;
	width: 0px!important;
	height: 0px!important;
	margin-top: 0px!important;
	border-bottom: solid 11px rgba(0,0,0,0.70)!important;
	border-right: 11px solid transparent!important;
	border-left: 11px solid transparent!important;
}

.selection_nipple:not([class="selection_nipple white"]) {
	border-bottom: solid 11px rgba(0,0,0,0.70)!important;
}

.mceContentBody {
	color: rgba(255,255,255,1.0)!important;
}

#content_top {
	width: 0px!important;
	height: 0px!important;
}

#content_bottom {
	width: 0px!important;
	height: 0px!important;
}

label {
	color: rgba(0,0,0,1.0)!important;
}

#footer {
	display: none!important;
}

.permalink {
	background: none!important;
	width: 16px!important;
	height: 16px!important;
	color: rgba(184,185,185,1)!important;
	-moz-border-bottom-left-radius: 10px !important;
	-webkit-border-bottom-left-radius: 10px !important;
	-ms-border-bottom-left-radius: 10px !important;
	-o-border-bottom-left-radius: 10px !important;
	border-bottom-left-radius: 10px !important;
	-webkit-border-top-right-radius: 24px !important;
	-ms-border-top-right-radius: 24px !important;
	-o-border-top-right-radius: 24px !important;
	-moz-border-top-right-radius: 24px !important;
	border-top-right-radius: 24px !important;
	margin-top: 6px!important;
	margin-right: 6px!important;
}

.permalink:hover {
	background: rgba(255,255,255,1)!important;
}

#open_blog_link {
	color: rgba(184,185,185,1)!important;
}


.identity-legacy .controls_section.user_list li .follow_list_item_blog:before {
	background: transparent!important;
	border-right: 0!important;
}

.identity-legacy .search_query,
.identity-legacy:not(.new_corp_pages) .search_query,
.identity-legacy:not(.new_corp_pages):not(.login-with-about) .search_query {
	background: #0c0c0c;
}

#search_query:hover,
.right_column_section li a:hover,
#right_column ul:first-child li:first-child a:hover,
#right_column ul:nth-child(3) li:first-child a:hover {
	background: rgba(255,255,255,0.05)!important;
	color: white!important;
}

.radar .radar_footer .radar_avatar:before {
	background: transparent!important;
}

.l-content clearfix #left_column {
	background: #fff!important;
}

.content.is_media,
.mask.top,.mask.bottom {
	background: none!important;
}

#dashboard_directory #tabs,
#main_explore #wire {
	margin-top: -25px!important;
}

div#right_column,
div#right_column:after {
	background: none!important;
}

#left_column,
#right_column {
	background-color: transparent!important;
}

.post.photo_post .photo,
.post.photo_post .video_thumbnail {
	-moz-box-shadow: 1px 1px 3px #ffffff!important;
	-webkit-box-shadow: 1px 1px 3px #ffffff!important;
	-o-box-shadow: 1px 1px 3px #ffffff!important;
	-ms-box-shadow: 1px 1px 3px #ffffff!important;
	box-shadow: 1px 1px 3px #ffffff!important;
	-moz-border-radius: 12px!important;
	-webkit-border-radius: 12px!important;
	-o-border-radius: 12px!important;
	-ms-border-radius: 12px!important;
	border-radius: 12px!important;
}

.post.link.not_mine:hover,
.post.quote.not_mine:hover,
.post.audio.not_mine:hover,
.post.video.not_mine:hover,
.post.regular.not_mine:hover,
.post.note.not_mine:hover,
.post.photo.not_mine:hover {
	border-top-right-radius: 24px!important;
	opacity: 1!important;
}

#posts_control {
	color: rgba(184,185,185,1)!important;
}

a.new_post_label > i {
    height: 90px;
}

.new_post_label/*_icon*/ > I {
    background: url('http://static.tumblr.com/x2hfity/jqzneqg9v/icons.png') no-repeat !important;
}

.new_post_buttons {
    transition: background 1s linear;
}

.new_post_buttons:not(:hover) {
    background: url('http://static.tumblr.com/x2hfity/V36neqg20/icons-bg.png') no-repeat !important;
}

.new_post_buttons:hover {
    background: url('http://static.tumblr.com/x2hfity/V36neqg20/icons-bg.png') no-repeat !important;
}

.new_post_label {
  	font-size: 0!important;
}

.new_post_label > I {
    color: transparent !important;
    transition: background-position .08s linear !important;
}

#new_post_label_text > I {background-position: 2px -5px !important}
#new_post_label_photo > I {background-position: -73px -5px !important}
#new_post_label_quote > I {background-position: -148px -5px !important}
#new_post_label_link > I {background-position: -223px -5px  !important}
#new_post_label_chat > I {background-position: -298px -5px !important}
#new_post_label_audio > I {background-position: -373px -5px !important}
#new_post_label_video > I {background-position: -448px -5px  !important}

#new_post_label_text:hover > I {background-position: 2px -9px !important}
#new_post_label_photo:hover > I {background-position: -73px -9px !important}
#new_post_label_quote:hover > I {background-position: -148px -9px !important}
#new_post_label_link:hover > I {background-position: -223px -9px  !important}
#new_post_label_chat:hover > I {background-position: -298px -9px !important}
#new_post_label_audio:hover > I {background-position: -373px -9px  !important}
#new_post_label_video:hover > I {background-position: -448px -9px  !important}

#new_post_label_text:active > I {background-position: 2px -5px !important}
#new_post_label_photo:active > I {background-position: -73px -5px !important}
#new_post_label_quote:active > I {background-position: -148px -5px !important}
#new_post_label_link:active > I {background-position: -223px -5px !important}
#new_post_label_chat:active > I {background-position: -298px -5px !important}
#new_post_label_audio:active > I {background-position: -373px -5px !important}
#new_post_label_video:active > I {background-position: -448px -5px !important}

#new_post .cog {
	margin-right: -15px!important;
}

#new_post .new_post_label {
	color: rgba(226,224,209,0)!important;
	line-height: 3.9em;
	font-weight: bold!important;
	font-size: .85em!important;
}

#new_post .main_content {
	background: rgba(255,255,255,0.95)!important;
	background-opacity: 0.95!important;
	color: rgba(0,0,0,0.7)!important;
	border: none!important;
}

#new_post .popover_inner.clearfix, #new_post .post_options .popover_inner, #tag_suggest.popover, .post_options.popover {
	background: rgba(255,255,255,0.95)!important;
}

#new_post_label_text,
#new_post_label_photo,
#new_post_label_quote,
#new_post_label_link,
#new_post_label_chat,
#new_post_label_audio,
#new_post_label_video {
	font: normal .85em verdana!important;
	text-transform: lowercase!important;
}

#categories .category,
#categories .category.active {
	color: rgba(255,255,255,.85)!important;
}

.selected .following,
.selected .followers,
.selected .settings,
.selected .posts,
.selected .messages,
.selected .drafts,
.selected .queue,
.selected .likes,
.selected .activity {
	color: rgba(255,255,255,1)!important;
	background: rgba(0,0,0,0.70)!important;
}

li.selected,
li.recessed,
a.active,
div.post_filter_tabs a {
	background: none !important
}





#home_button {
    padding-right: -17px !important;
}

#home_button a {
    height: 0!important;
    width: 0!important;
    top: 0!important;
    padding: 36px 0 0 36px!important;
    background: url('http://static.tumblr.com/x2hfity/niOneqjz9/menu-home.png') !important;
}

#inbox_button {
    padding-right: -17px !important;
}

#inbox_button a {
    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 36px !important;
    background: url('http://static.tumblr.com/x2hfity/flNneqlg2/menu-inbox.png') !important;
}

#preferences_button {
    padding-right: -17px !important;
}

#preferences_button a {
    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 36px !important;
    background: url('http://static.tumblr.com/x2hfity/B7eneqkba/menu-logoff.png') !important;
}

#help_button {
    padding-right: -17px !important;
}

#help_button a {
    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 36px !important;
    background: url('http://static.tumblr.com/x2hfity/IYXneqk7a/menu-help.png') !important;
}

#xkit-control {
    padding-right: -17px !important;
}

#xkit-control a.tab_anchor {
    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 36px !important;
    background: url('http://static.tumblr.com/x2hfity/IYXneqk7a/menu-help.png') !important;
}

.tab.iconic.tab_log_out a {
    padding-right: -17px !important;
}

.tab.iconic.tab_log_out a {
    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 36px !important;
    padding-top: 36px !important;
    background: url('http://static.tumblr.com/x2hfity/zT0neqk4n/menu-settings.png') !important;
}

.tab_bar .iconic a.tab_anchor {background-size: 175px 36px;}
.tab_bar .tab_home .tab_anchor {background-position: 0 0}
.tab_bar .tab_inbox .tab_anchor {background-position: -35px 0}
.tab_bar .tab_help .tab_anchor {background-position: -70px 0}
.tab_bar .tab_settings .tab_anchor {background-position: -105px 0}
.tab_bar .tab_log_out .tab_anchor {background-position: -140px 0}
.tab_bar .tab_discover .tab_anchor {background-position: -175px 0}
.tab_bar .tab.iconic:hover .tab_anchor,.tab_bar .tab.iconic:active .tab_anchor {opacity: .7;}
.tab.iconic > .tab_anchor::before {content: none !important;}


#dashboard_iframe,
.dashboard_iframe {
    background: none!important
}

}
