/* latin-ext */

@font-face {
    font-family: 'Bitter';
    font-style: italic;
    font-weight: 400;
    src: local('Bitter-Italic'), url(https://fonts.gstatic.com/s/bitter/v7/PX9NwOMMeyM1S_WWulmbfhTbgVql8nDJpwnrE27mub0.woff2) format('woff2');
    unicode-range: U+0100-024F, U+1E00-1EFF, U+20A0-20AB, U+20AD-20CF, U+2C60-2C7F, U+A720-A7FF;
}
/* latin */

@font-face {
    font-family: 'Bitter';
    font-style: italic;
    font-weight: 400;
    src: local('Bitter-Italic'), url(https://fonts.gstatic.com/s/bitter/v7/-t3SK6sofTjAH0MNf_tLaPesZW2xOQ-xsNqO47m55DA.woff2) format('woff2');
    unicode-range: U+0000-00FF, U+0131, U+0152-0153, U+02C6, U+02DA, U+02DC, U+2000-206F, U+2074, U+20AC, U+2212, U+2215, U+E0FF, U+EFFD, U+F000;
}
html {
    background: #000000 !important;
    color: #FFFFDC !important;
    margin: 0 auto !important;
    padding: 0px !important;
    width: 1000px !important;
    font-family: 'Bitter', 'Courier New', monospace !important;
}
body {
    background: #000000 !important;
    color: #EEEEEE !important;
}
font[size="+7"] {
    line-height: 42px !important;
}
font[color="BLUE"] {
    color: #4F81DF !important;
}
a {
    color: #EEEEEE !important;
}
a font[color="BLUE"] {
    color: #4F81DF !important;
}
a:visited {
    color: #676767 !important;
}
a:visited font[color="RED"] {
    color: #774040 !important;
}
a:visited font[color="BLUE"] {
    color: #24468C !important;
}
a:hover {
    transition: color 0.75s ease !important;
    color: #000000 !important;
    transition: background-color 0.75s ease !important;
    background-color: #EEEEEE !important;
    text-decoration: none !important;
}
a:hover font[color="RED"] {
    transition: color 0.75s ease !important;
    color: #774040 !important;
}
a:hover font[color="BLUE"] {
    transition: color 0.75s ease !important;
    color: #24468C !important;
}
a:visited[href=""] {
    color: #EEEEEE !important;
}
a:visited[href=""] font[color="RED"] {
    color: red !important;
}
td[width="3"] {
    display: none !important;
}
td[width="728"] {
    display: none !important;
}
table {
    margin: -15px 0 0 0 !important;
    padding: 0px !important;
}
tr td tt b,
tt b a font,
tt b a,
tt b {
    font-family: 'Bitter', Helvetica, Verdana, Arial, sans-serif !important;
    font-size: 11.5px !important;
}
td {
    /* the curved things at the top of each column */
    
    width: 280px !important;
    margin: 0px !important;
    padding: 15px 15px 0 20px !important;
    border: none !important;
    background: url("data:image/png;base64,") no-repeat top center !important;
}
td tt img {
    /* makes column images a little more dignified */
    
    display: block !important;
    width: 200px !important;
    padding: 0 50px !important;
    margin-left: auto !important;
    margin-right: auto !important;
    margin-bottom: 3px !important;
    background: url("data:image/png;base64,") repeat-y center !important;
}
tt b font font a,
tt b font a i,
tt b font font {
    font-family: 'Bitter', Helvetica, Verdana, Arial, sans-serif !important;
    font-size: 42px !important;
    font-style: normal !important;
}
td tt b br {
    display: none !important;
}
td tt b a {
    display: block !important;
}
td font[size="-5"] {
    display: none !important;
}
hr {
    border: none !important;
    border-top: 1px dotted #333 !important;
    border-bottom: 1px dotted #555 !important;
    color: #2B2B2B !important;
    background-color: #2B2B2B !important;
    margin: 7px 42px !important;
}
img[src*="logo9.gif"] {
    /* Original logo copyright Drudge Report. Modified Drudge hat logo copyright Stephen VanDyke. All credit due where it's deserved. */
    
    height: 100px !important;
    width: 0px !important;
    padding-left: 605px !important;
    padding-right: 0px !important;
    padding-top: 0px !important;
    padding-bottom: 0px !important;
    margin-top: 15px !important;
    background: url("data:image/png;base64,") no-repeat center center !important;
    overflow: hidden !important;
}
}
