css:
@-moz-document domain("weibo.com") {
/* None items */
.WB_global_nav .gn_nav,
.WB_global_nav .gn_person,
#pl_leftnav_app,
#trustPagelet_indexright_recom,
#pl_content_setSkin,
#trustPagelet_recom_memberv5,
#pl_rightmod_ads36,
#pl_rightmod_noticeboard,
.W_person_info .nameBox .icon_bed,
.send_weibo .title_area,
.global_footer,
.WB_webim,
.group_read,
div[feedtype="ad"],
.ico_member,
.ico_club,
.W_ico16,
.user_atten,
.send_weibo .input .arrow,
div[node-type="feed_spread"],
.tips_wrapper,
#pl_leftnav_common div.level_1_Box:last-child,
.WB_global_nav .gn_logo,
a[action-type="feed_list_like"],
.WB_feed .WB_func .WB_handle i.S_txt3,
.WB_from .S_txt2,
.WB_from a:nth-child(3),
.WB_feed_type .WB_detail > .WB_func,
.WB_feed .WB_feed_together {
  	display:none !important;
  }

/* Globle */
body {
    background:#E6E6E6 !important;
  }
          
.S_bg1 {
    background-color:#FCFCFC !important;
  }

.S_line1 {
  	border-color:#DADADA !important;          
  }

.S_line1_c {
    color:#DADADA !important;
  }
                
.S_bg1_c {
    color:#FCFCFC !important;      
  }

/* Header */  
.WB_global_nav {
    background:#676767 !important; 
  	-webkit-box-shadow:0 0 0 0 transparent;
	height:40px !important;
    /*overflow:hidden !important;*/
    border-bottom:1px #000 solid !important;
    z-index:9998 !important;
  }
        
.WB_global_nav .gn_header {
    width:800px !important;
  }
  
.WB_global_nav .gn_bg {
  	background:none !important;
  	background:-webkit-gradient(linear, 0 0, 0 bottom, from(#4D4D4D), to(#333333)) !important;
  	height:39px !important;
  }  
  
.WB_global_nav .gn_logo {
  	position:static !important;
  	float:left !important;
  	margin:0 !important;
  }

.WB_global_nav .gn_search {
	float:right !important;
  }

/* body */
.W_miniblog, .S_profile .W_miniblog {
	background:none !important;
  }

.W_main {
  	background:none !important;
  	width:800px !important;
  }

/* body | left */
.W_main_l {
	width:200px !important;
  	position:relative !important;
  	overflow:visible !important;
  }
.B_index .W_main_l {
	padding:280px 0 30px !important;
  }

/* body | middle */
.W_main_a {
	width:auto !important;
  	overflow:visible !important;
  	position:relative !important;
  }

.W_main_c {
	width:600px !important;
  	overflow:visible !important;
  }

.B_index .W_main_c {
	padding:10px 0 50px !important;
  }

/* body | right */
.B_index .W_main_r {
	position:static !important;
  	padding:0 !important;
  }

.W_main_r {
	overflow:visible !important;
  	width:0 !important;
  	height:0 !important;
  	float:none !important;
  	font-size:0 !important;
  }

/* Post Weibo */
.send_weibo {
  	padding:0 0 10px 20px !important;
  }

/* Feeds */
.WB_feed_type {
	position:relative;
  }

.WB_feed .WB_screen {
  	margin:0 !important;
    right:23px !important;
    top:43px !important;
    z-index:10 !important;
  }

.WB_feed a {
	color:#507DAF !important;
  	text-decoration:none !important;
  }

.WB_feed a:hover {
	color:#507DAF !important;
  	text-decoration:underline !important;
  }

.WB_feed .WB_feed_datail:hover {
  	border-color:rgba(82, 168, 236, 0.8) !important;
    -webkit-box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(82, 168, 236, 0.6) !important;
    box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.075), 0 0 8px rgba(82, 168, 236, 0.6) !important;
  }

.WB_feed .WB_face .W_face_radius {
	display:block !important;
  	border-radius:50px !important;
  	width:50px !important;
  	height:50px !important;
  }

.WB_feed .WB_feed_type {
  	margin:0 0 0 20px !important;
  }

.WB_feed .WB_feed_datail {
  	background:#FFF !important;
  	border:1px #D5D5D5 solid !important;
  	padding:15px !important;
  	border-radius:3px !important;
  	position:relative !important;
  }

.WB_feed .WB_face {
	position:absolute !important;
  	left:15px !important;
  	top:15px !important;
  	float:none !important;
  }

.WB_feed .WB_detail {
	margin:0 !important;
  	padding-top:60px !important;
  }

.WB_feed_type .WB_detail > .WB_info {
	margin:-50px 0 0 65px !important;
  	height:30px !important;
  	line-height:30px !important;
  	font-size:16px !important;
  	font-weight:bold !important;
  }

.WB_feed_type:hover .WB_detail > .WB_func {
    display:block !important;
	position:absolute !important;
  	left:80px !important;
  	top:45px !important;
  	height:20px !important;
  	line-height:20px !important;
  	width:480px !important;
  }

.WB_feed_type .WB_detail > .WB_text {
	margin-top:20px !important;
  }

.WB_feed .WB_media_expand {
  	margin:5px 0 !important;
  }

.WB_feed .WB_func .WB_handle a {
	margin:0 5px !important;
  }
        
/* Nav */
#pl_leftnav_common {
	position:fixed !important;
  	left:0 !important;
  	top:0 !important;
  	z-index:9999 !important;
  	width:100% !important;
  	height:39px !important;
  	margin:0 auto !important;
  	pointer-events:none !important;
   }

#pl_leftnav_common div.level_1_Box {
	width:800px !important;
  	margin:0 auto !important;
  	padding:0 !important;
   }

.WB_left_nav .level_1_Box .lev a {
	color:#AAA !important;
  	height:39px !important;
  	line-height:40px !important;
  	padding:0 15px !important;
  	pointer-events:auto !important;
   }

.WB_left_nav .level_1_Box .lev a:hover,
.WB_left_nav .level_1_Box .lev a.lev_curr, 
.WB_left_nav .level_1_Box .lev a.lev_curr:hover{
	color:#FFF !important;
  	background:none !important;
  	background:-webkit-gradient(linear, 0 0, 0 bottom, from(#444444), to(#191919)) !important;
   }

/*#pl_leftnav_commo .level_1_Box:after {
  	content:"";
  	clear:both;
  	display:block;
  	height:0;
   }*/

#pl_leftnav_common .lev {
	float:left !important;
   }

#pl_leftnav_commo .lev a, 
#pl_leftnav_commo .lev2 a {
	color:#FFF !important;
   }

/* Person Info */
.W_person_info {
	position:absolute !important;
  	left:-200px !important;
  	top:20px !important;
  	width:180px !important;
  	height:260px !important;
  	/*background:#DE0000 !important;*/
    margin:0 !important;
  }

.W_person_info dt {
    float:none !important;
    width:auto !important;
    height:auto !important;
    margin:0 !important;
    padding:0 !important;
    background:none !important;
    margin-bottom:10px !important;
  }
          
.W_person_info dt a {
    border-radius:180px !important;
    display:block !important;
    height:180px !important;
    width:180px !important;
    overflow:hidden !important;
  }
        
.W_person_info dt img {
    border:0 none !important;
    width:180px !important;
    height:180px !important;
  }

.W_person_info .nameBox {
  	text-align:center !important;
  }
  
.W_person_info .nameBox a {
    display:inline-block !important;
    font-size:14px !important;
    width:160px !important;
    text-align:center !important;
  }
  
.W_person_info .nameBox .name {
	line-height:160% !important;
  }
}
