css:
@-moz-document domain("www.blender.org") {
div.body h1,
div.body h2,
div.body h3,
div.body h4,
div.body h5,
div.body h6 {
    font-family: 'Trebuchet MS', sans-serif;
    background-color: #443344;
    font-weight: bold;
    color: #bee11a;
    border-bottom: 5px solid #222222;
    margin: 20px -20px 10px -20px;
    padding: 3px 0 3px 10px;
}

div.document {
    background-color: #444444 ;
}

div.body {
    background: #222222;
    color: #888888;
    padding: 1px 20px 30px 20px;
}


div.related {
    background-color: #333333 ;
}


pre {
    background-color: #9eaf9e ;
}

span.gr{
color: #9f2222 !important;
}


a{
color: #000000 !important;
}

a:visited{
color: #4297e5 !important;
}

a:link{
color: #bee11a !important;
font-weight: bold !important;
}

a:hover, a:hover span {
color: #fbe54e !important;
text-decoration: underline !important;
}

th {
    background-color: #443344;
}

tt {
    background-color: #663355;
    padding: 0 1px 0 1px;
    font-size: 0.95em;
}

.warning tt {
    background: #771111;
}


dt:target, .highlighted {
    background-color: #663355;
    color: #fbe54e;
    font-weight: bold;
}


div.sphinxsidebar #searchbox input[type="text"] {
    width: 145px;
    background: #222222;
    color: #fbe54e;
}

div.sphinxsidebar #searchbox input[type="submit"] {
    width: 35px;
    background: #333333;
    color: #fbe54e;
}

div.sphinxsidebar h4 {
    font-family: 'Trebuchet MS', sans-serif;
    color: #999999;
    font-size: 1.3em;
    font-weight: normal;
    margin: 5px 0 0 0;
    padding: 0;
}

div.sphinxsidebar h3 {
    font-family: 'Trebuchet MS', sans-serif;
    color: #999999;
    font-size: 1.4em;
    font-weight: normal;
    margin: 0 ;
    padding: 0;
}

div.sphinxsidebar p {
    color: #888888;
}

div.sphinxsidebar ul {
    margin: 10px;
    padding: 0;
    color: #222222;
}

div.sphinxsidebar a {
    color: #222222;
}

div.sphinxsidebar input {
    border: 1px solid #222222;
    font-family: sans-serif;
    font-size: 1em;
}

tt.descclassname {
  	color: #fba51e;
}

tt.descname {
  	color: #fbe54e;
    font-weight: bold;
}

td.field-body strong {
  	color: #a5a5a5;
}

span.pre,
tt.docutils{
    background: #222222;
  	color: #af1163;
}
}
