css:
body {
  background-image: none !important;
  background-color: white !important;
  font-size: 100% !important;
  color: #222;
  font-family: Lato, "Helvetica Neue", Helvetica, Arial, sans-serif;
}

div#content {
  max-width: 48em;
}

div#header {
  background-image: none;
  margin-bottom: 0px;
}

#header ul {
  border-bottom-color: #eee;
}

div#header h1 a {
  text-indent: 0px;
  background-image: none;
  margin-left: 20px !important;
  font-weight: 100;
  color: #222 !important;
}

div#content {
   background-image: none !important;
   padding-left: 1.5em !important;
   padding-right: 1.5em !important;
   width: auto !important;
}
div#header {
   margin-left: auto !important;
   margin-right: auto !important;
   width: 870px !important;
}

#content h2 { color: #80a4e4 !important; }
#home h2, #home h3 {
   font-weight: bold !important;
   margin-top: 2em !important;
   text-decoration: underline;
}

pre {
   font-size: 120% !important;
   line-height: normal !important;
   overflow: auto !important;
}

div.highlight {
   margin-top: 5px !important;
   margin-bottom: 15px !important;
   padding-top: 5px !important;
   padding-bottom: 5px !important;
}



div.highlight pre .nf { color: #a2bad8 !important; }
div.highlight pre .o { color: #ffff92 !important; }

div.highlight pre .c1 {
   font-style: normal !important;
   color: #ff80c0 !important;
}

#home pre {
   margin-right: 100px !important;
}

#content .highlight pre {
   padding: 2px 8px !important;
}



p code {
   font-size: 120% !important;
   background-color:   #153c6c !important;
   color: silver !important;
   padding: 2px !important;
}

dt a {
  color: #44aacc !important;
}

#content a:link {
  color: #44aacc !important;
}

#content a:visited {
  color: #55bbdd !important;
}

#content pre, p code {
  background-color: #eed !important;
  color: #222 !important;
  border: 0px;
}


#footer {
  background-image: none;
  border-top: 1px solid #eee;
}

#content h2 {
  color: #222 !important;
  font-weight: 100;
  text-decoration: none;
}

div.highlight pre .nb, div.highlight pre .no, div.highlight pre .nc {
  color: #555;
}

h3, #home p, #home pre, #home .puff {
  margin-right: 0px;
}

#content a#getStarted {
  background-image: none;
  background-color: #44aacc;
  text-indent: 0px;
  color: white !important;
}

#content a#getStarted:visited {
  color: white !important;
}

#help, #bugs {
  background-image: none;
  background-color: transparent;
  color: #222 !important;
  width: 100%;
  float: none;
  padding-left: 0px;
  height: auto !important;
}

h2.latest-release, p.latest-release, #help {
  background-color: transparent;
  margin-left: 0px;
}

div.highlight pre .o {
  color: #555 !important;
}

div.highlight pre .vi {
  color: #304F5E
}
