css:
@-moz-document url("http://www.tumblr.com/"), url("http://www.tumblr.com/dashboard"), url("http://www.tumblr.com/likes"), url("http://www.tumblr.com/following"), url("http://www.tumblr.com/inbox"), url-prefix("http://www.tumblr.com/blog/"), url-prefix("http://www.tumblr.com/tagged/"), url-prefix("http://www.tumblr.com/activity/") , url("https://www.tumblr.com/"), url("https://www.tumblr.com/dashboard"), url("https://www.tumblr.com/likes"), url("https://www.tumblr.com/following"), url("https://www.tumblr.com/inbox"), url-prefix("https://www.tumblr.com/blog/"), url-prefix("https://www.tumblr.com/tagged/"), url-prefix("https://www.tumblr.com/activity/") {
body {
   background:url("http://i42.tinypic.com/2qt8l08.png")no-repeat fixed  !important;
   background-color: #afc371 !important;
   background-position: 0% 60% !important;
   background-size: 100% 100%  !important;
 }

div.l-header .tab.iconic a{
  background-color: rgba(216,173,157,1.0) !important;
  border-radius: 60px !important;  
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {  
   font-family: Arial; color: #afc371;
   background: rgba(216,173,157,0.75) !important;

 }


.notification  {
  	background: #afc371 !important; border: none !important; 
	color: #fff !important; font-size: 11px !important;
  }

.notification :link, .notification :active, .notification :visited {
  	color: #ffffff !important;
    text-decoration: none !important;
    border: none !important;
  }

.new_post_label .new_post_label_icon {
background-image: url('http://i39.tinypic.com/2mgrp7l.png') !important;
background-size: 520px 100px !important;
margin-top: 0px !important;
margin-right: 15px !important;
opacity: 1 !important;
}

div.l-content #new_post .new_post_label {
color: rgba(255,255,255,.9) !important;
line-height: 1.4em;
font-weight: bold !important;
font-size: .9em !important;

}

#right_column .controls_section li a,

#right_column .controls_section li a .count {

  color: #ffffff !important; 
}

div#right_column,
div#right_column:after {
  background: none !important;
}

#logo, #logo a {
  position: relative;
  z-index: 1;
  width: 270px !important;
  height: 71px !important;
  background-repeat: no-repeat;
  background: url("http://i42.tinypic.com/35bgvuh.png")no-repeat !important;
}

.controls_section li .posts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.posts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .posts:before {
  content: "Leaves" !important;
}.controls_section li .followers:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.followers  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .followers:before {
  content: "Adventurers" !important;
}.controls_section li .drafts:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.drafts  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .drafts:before {
  content: "Draft" !important;
}.controls_section li .queue:before {

  position: absolute !important;
  height: 30px !important;
  line-height: 28px !important;
  width: 100px !important;
  font: inherit !important;
}

a.queue  div.hide_overflow{
  visibility: hidden !important;
}

.controls_section li .queue:before {
  content: "Queue" !important;
}

.selection_nipple.white, .selection_nipple:not([class="selection_nipple white"]) {

  background: none !important;

  width: 0px !important;

  height: 0px !important;

  margin-top: 0px !important;

  border-bottom: solid 11px rgba(220,243,148,0.25) !important;

  border-right: 11px solid transparent !important;

  border-left: 11px solid transparent !important;

}
}

@-moz-document url("http://www.tumblr.com"), url("http://www.tumblr.com/dashboard") , url("https://www.tumblr.com"), url("https://www.tumblr.com/dashboard") {
#posts .post .post_permalink {
width: 40px !important;
height: 40px !important;
background: url('http://i44.tinypic.com/2hntgt4.png')!important;
background-size: 100% 100% !important;
position: absolute !important;
top: 6px !important;
right: -28px !important;
}

#posts .post .post_permalink:hover {
background: url('http://i44.tinypic.com/2hntgt4.png') !important;
width: 40px !important;
height: 40px !important;
background-size: 100% 100% !important;
position: absolute !important;
top: 6px !important;
right: -28px !important;
}

}
