css:
@-moz-document domain(stellarwinds.me) {


/* Links */
a:hover {
    color: #780000 !important;
    background-color: #B0B0B0 !important;

}



/* Navbar */
.navbar-fixed-top,.navbar-inner,.navbar {
    background-color: #202020 !important;
    color: #B0B0B0 !important;
    border-color: #101010 !important;
    background-image: none !important;
}
.navbar-fixed-top a,ul.dropdown-menu {
    color: #B0B0B0!important;
    text-shadow: 0px 0px 0px #580000 !important;
    font-weight: bold !important;
    background-color: #202020 !important;
    font-size: 16px !important;
}
ul a:hover {
    background-image: none !important;
    background-color: #580000 !important;
    color: #D0D0D0 !important;
}
.dropdown-menu > li > a:hover {
    background-image: none !important;
    background-color: #580000 !important;
    color: #D0D0D0 !important;
}
.navbar li.divider-vertical {
    display: none !important;
}
input[type=search] {
    background-color: #202020 !important;
    color: #D0D0D0 !important;
    border-color: #404040 !important;
    box-shadow: 0px 1px 1px #404040 inset, 0px 0px 8px #680000 !important;
}
select {
    background-color: #202020 !important;
    color: #D0D0D0 !important;
    border-color: #580000 !important;
    background-image: none !important;
    opacity: .7 !important;
}
#topsearch > button:nth-child(3) {
    font-size: 14px !important;
    color: #980000 !important;
    opacity: .7 !important;   
}
img[id=inbox] {
    display: none !important;
}
#stats ul.nav a {
    color: #D0D0D0 !important;
}
ul.nav a.brand * {
    color: #000 !important;
    text-shadow: 0px 1px 0px #680000;
}
.pull-right a {
    color: #B0B0B0 !important;
}

a.brand img {
    color: #D0D0D0 !important;
    width: 85% !important;
    height: 25px !important;
    background-image: -moz-linear-gradient(top, #580000 6%, #101010 80%);
    border-color: #505050 !important;
    border-radius: 10px !important;
    padding-right: 10px !important;
    padding-left: 10px !important;
    padding-bottom: 6px !important;
    margin-top: -6px !important;
    padding-top: 6px !important;
    box-shadow: 3px 3px 3px 1px #303030;
    margin-bottom: 4px !important; 
}

/* Body */

body {
    background-color: #101010 !important;
}

#main {
    background-color: #101010 !important;
    color: #D0D0D0 !important;
    box-shadow: 0px 10px 12px #780000 !important;
}
.alert-info,.alert-info a {
    background-color: #202020 !important;
    color: #D0D0D0 !important;
    border-color: #780000 !important;
}
.module {
    background-color: #202020 !important;
    box-shadow: 0px 0px 5px 0px #505050 !important;
    color: #D0D0D0 !important;
}
.module a {
    color: #909090 !important;
}
.module p * {
    color: #D0D0D0 !important;
}
.module h4,h3,h4 * {
    color: #B0B0B0 !important;
}

/* Browse */

table.wishlist tr * {
    background-color: #202020 !important;
    color: #D0D0D0 !important;
    border-color: #780000 !important;
}
.rounded-right {
    background-image: none !important;
    background-color: #202020 !important;
    border-color: #780000 !important;
    color: #B0B0B0 !important;
}
.pagination-centered a {
    background-color: #101010 !important;
    color: #D0D0D0 !important;
}
input[type=submit] {
    opacity: .7 !important;
}
input[type=button] {
    opacity: .7 !important;
}

/* Torrent View */
.span4 .table-bordered,.table-striped * {
    border-color: #780000 !important;
    color: #D0D0D0 !important;
    background-color: #202020 !important;
}
.table-bordered a.btn {
    background-image: none !important;
    background-color: #580000 !important;
}
a.btn-primary,a.btn,a.pull-right {
    background-image: none !important;
    background-color: #580000 !important;
    border-color: #000 !important;
}
a.btn {
    background-color: #580000 !important;
    color: #D0D0D0 !important;
    border-color: #000 !important;
}
.rounded {
    border-color: #780000 !important;
}

/* Gallery */

#page {
    background-color: #202020 !important;
    border-color: #101010 !important;
}
#cboxWrapper * {
    background-color: #202020 !important;
    color: #D0D0D0 !important;
}
#cboxClose {
    opacity: .7 !important;
}
#footer a {
    color: #780000 !important;
}

/* Top Ten */

td.embedded a {
    color: #B0B0B0 !important;
}

/* upload */

form[action^=takeupload] input,textarea {
    background-color: #202020 !important;
    color: #D0D0D0 !important;
    border-color: #780000 !important;
}

/* Forums */

#brdtitle,#brdwelcome,#announce .box,#announce a,#vf .box *,#searchform .box,#brdfooter .inbox,#brdfooter a {
    background-color: #202020 !important;
    color: #D0D0D0 !important;
    border-color: #101010 !important;
}
#brdmenu,#brdmenu a,#announce h2,#vf h2,#searchform h2 {
    background-color: #780000 !important;
    color: #D0D0D0 !important;
}
tr.inew * {
    border-color: #101010 !important;
    color: #D0D0D0 !important;
}
#idx1 .inbox *,#idx2 .box *,#idx3 .box *,#idx4 .box *,#idx5 .box *,#idx6 .box * {
    background-color: #202020 !important;
    color: #D0D0D0 !important;
    border-color: #101010 !important;
}  
#idx1 h2,#idx2 h2,#idx3 h2,#idx4 h2,#idx5 h2,#idx6 h2 {
    background-color: #780000 !important;
    color: #D0D0D0 !important;
    border-color: #101010 !important;
}  
.postmsg {
    color: #000 !important;
}


}
