@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dict.leo.org") {
    
#leftColumn,#right,div.nav:nth-child(4),#resultOrdering,
#searchBoxContainerRight,#inputHelperActivation,#clearLocation,
#switchSearchMode,#entriesAndQueries,#miniSwitcher,#topBranding,
#topMenu,#footer,#searchBoxContainerLeft {
display:none!important
}

#centerColumn {
position:inherit!important;
top:-100px!important;
left:0px!important;
width:99%!important
}

#body {
min-width:100%!important;
width:100%!important
}

.breadcrumb {
text-align:center!important
}

#searchBoxContainerCenter {
left:auto!important;
}

}
