css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("last.fm") {

/** Change Link Colors **/
#secondaryNavigation ul li.current a,
#secondaryNavigation ul li a:hover
 {
  color: rgb(55, 95, 135) !important;
 }
#idBadger, #headLinks, #headLinks a
 {
  color: white !important;
 }


/* Simplify Scrobble Counter */
#userBadge .badgeInfo .userPlays span.flip
 {
  background-image: none !important;
  background-color: rgb(55, 95, 135) !important;
  border: 1px solid black !important;
  border-left: none !important;
  padding: 0 0 2px 2px !important;
  margin: 0 !important;
  color: rgb(225, 225, 225) !important;
 }
#userBadge .badgeInfo .userPlays span.flip:first-child
 {
  -moz-border-radius-topleft: .6em !important;
  -moz-border-radius-bottomleft: .6em !important;
  padding: 0 0 2px 2px !important;
  border-left: 1px solid black !important;
 }
#userBadge .badgeInfo .userPlays span.flip:last-child
 {
  -moz-border-radius-topright: .6em !important;
  -moz-border-radius-bottomright: .6em !important;
 }

/* Change sprite for modified version */

#fauxHeader, #lastfmLogo, #primaryNav .nav-link:hover, #profileLinks .divider a, #loginLink .profile-link, #loginLink .profile-link strong, #idBadgerUser, #idBadgerDropper, #siteSearchBox, #siteSearchSubmit, #headerLangToggle img.globe_icon
 {
  background-image: url('data:image/png;charset=utf8;base64,') !important;
 }

/* Change dropdown color */

#idBadgerDropDown, #langSelector.js
 {
  background-color: rgb(35, 70, 105) !important;
 }

#idBadgerDropDown a:hover, #langSelector.js a:hover
 {
  background-color: rgb(55, 95, 135) !important;
 }
}
