style_setting_options.value:
/* USUNIĘCIE INFORMACJI NA DOLE 'Korzyanie z serwisu oznacza akceptacje regulaminu' */
footer.separator.separator-top .main-footer-additional-info,
footer.main-footer div.main-footer-additional {display:none !important;}

/* USUNIĘCIE POLECANYCH I OKAZJE W LIŚCIE KATEGORII MENU */
.layer-content.image-small .product,
.layer-content.image-small .image,
.layer-content.image-big .image{display:none !important;}
/*.main-nav > ul .nav-tab-container .nav-layer {width:380px !important; height:285px !important;} */

/* USUNIĘCIE BANNERÓW PROGRAMU OCHRONY KUPUJĄCYCH, MOBILNEGO ALLEGRO I RAT Z KAŻDEJ STRONY AUKCJI */
div.bannerContainer > a[rel="external"]{display:none !important;}
div.bannerContainer {height: 1px !important; margin-bottom: 0px !important; padding: 8px !important;}

/* WYŁĄCZENIE POLECANYCH ARTYKUŁÓW */
div[data-box-name="polecane artykuły"],
div.lazyloaded[data-box-name="layout.container_artykuly"]{display:none !important;}

/* 4 KOLUMNY WSZYSTKICH KATEGORII */
.modal-dialog.modal-lg {width: 1100px !important;}
.modal-dialog .list-container .all-links > .link {width: 25% !important;}

/* USUNIĘCIE OFERT SPONSOROWANYCH Z LISTY OFERT */
div.sas-placement,
section.offers-sponsored,
div#adsallegro-placement-left,
div.sky-scraper,
div.sas-dbb-placement[id*="sas_"]{display:none !important;}

/* ZMNIEJSZENIE LIST W MOIM ALLEGRO */
@-moz-document regexp("http://allegro.pl/myaccount/.*") {
.fb .fb-listing > tbody > tr > td {padding: 0px 10px !important;}
}

/* POZOSTAWIENIE OBSERWOWANYCH I OSTATNIO PRZEGLĄDANYCH OFERT NA STRONIE GŁÓWNEJ */
div.carousel-namespace>div.bargains,
div.carousel-namespace>div.recommended {display:none !important;} 
.separator.separator-top {border-top: 0px !important;}

/* WYŁĄCZENIE OBSERWOWANYCH I OSTATNIO PRZEGLĄDANYCH OFERT ZE STRONY GŁÓWNEJ */
div.carousel-namespace>div.bargains,
div.carousel-namespace>div.recommended,
div.carousel-namespace>div.watched-items,
div.carousel-namespace>div.last-visited {display:none !important;} 
.separator.separator-top {border-top: 0px !important;}

/* WYŁĄCZENIE "Oficjalny sklep Allegro" NA STRONIE DZIAŁU */
div[data-box-name="Oficjalny sklep Allegro"],
div[data-box-name="layout.container"],
div[data-box-name="oferty z osa"],
div.lazyloaded[data-box-name="layout.container_kampanie"]{display:none !important;}

/* WYŁĄCZENIE STREFY MAREK */
div[data-box-name="logotypy sma"],
div[data-box-name="sm.logotypy"],
div[data-box-name="layout.container_logotypes"],
div.lazyloaded[data-box-name="layout.container_sm"]{display:none !important;}

@-moz-document domain("archiwum.allegro.pl") {
/* ARCHIWUM ALLEGRO */
  
/* WYŁĄCZENIE - OSTATNIO ZARCHIWIZOWANE -- ARCHIWUM ALLEGRO -- WYSZUKIWANIE */
div#archiwum-listing>div.content>div.slider,
div#archiwum-listing>div.content>div.separator-bottom:nth-of-type(3n),
div.separator:nth-child(3){display:none !important;}

}

/* WYŁĄCZENIE - "POZOSTAŁE PRZEDMIOTY OD TEGO SPRZEDAWCY" oraz "PRODUKTY POLECANE PRZEZ SPRZEDAJĄCEGO" -- STRONA AUKCJI  */
div#SHOW_ITEM_ID.recommended,
div.carousel-namespace>div.carousel-container{display:none !important;}
