css:
@-moz-document url("http://www.twitch.tv/chat/embed?channel=saltybet&popout_chat=true"), domain("saltybet.com") {
body,
#chat,
#wagerwrapper,
#status,
#fightcard,
#bottomcontent,
#landing {
   background: #111 !important;
   color: #ddd !important;
}

.chat_from_saltybet.line {
  background: #222 !important;
}

#wagerwrapper a,
#status a,
#footer a,
#header a {
 color: #0B8A9B !important;
}


#header {
	background: #0d0d0d;
  	border: 0 !important;
    padding: 10px !important;
}

#stream,
#content {
	top: 60px !important;
}


#sbettorswrapper #sbettors1 {
	text-align: center !important; 
}

#sbettorswrapper #bettors1,
#sbettorswrapper #bettors2,
#sbettorswrapper #sbettors1 .redtext,
#sbettorswrapper #sbettors2 .bluetext {
	text-align: left !important;
}

#bettors1,
#bettors2 {
	border-top: 0 !important; 
}

#bettors1 .redtext,
#bettors2 .bluetext {
	 color: #555 !important;
}

a[style="color:#0000FF"]{
color: dodgerblue !important;
}

#chat_text_input{
  background: #555 !important;
  color: #fff !important;
}

#sbettors1 > .redtext,
#sbettors2 > .bluetext {
	display: block !important; 
  	font-size: 1.4em !important;
    margin-bottom: 3px !important;
}

#sbettors1 > .redtext {
  color: red !important;
}

#sbettors2 > .bluetext {
  color: #3787d6 !important;
}

.error {
  margin-bottom: -20px;
  color: rgba(0,0,0,0.65) !important;
  font-weight: bold;
  text-transform: uppercase;
}


.greentext { 
  color: #0B8A9B !important;
}

.goldtext {
  color: #ffad01 !important;
}

#result,
#landing {
  box-shadow: none !important;
}

#pagination {
 text-align: center !important; 
}

#pagination .right,
#pagination .left {
 	 float: none !important;
  	margin: 1em !important;
}

table {
  outline: 5px solid #000 !important;
}

.dataTables_filter {
   margin-bottom: 15px !important; 
  }

table,
th,
tr,
td {
 border-collapse: collapse !important; 
  border: 1px solid #222 !important;
}

th {
  background: #000 url('http://ajax.aspnetcdn.com/ajax/jquery.dataTables/1.9.4/images/sort_both.png') no-repeat center right !important;
  font-size: 1.5em !important;
  line-height: 2 !important;
}


tr.odd > td:nth-child(1),
tr.odd {
  background: #0b0b0b !important;
}

tr.even > td:nth-child(1),
tr.even {
  background: #111 !important;
}

.betcard {
	background: #333 !important;
    padding: 9px !important;
  	margin: 25px 0 !important;
}


  
#p1name,
#player1wager {
   float: left !important; 
  }
  
#p2name,
#p2ayer1wager {
   float: right !important; 
  }


::-webkit-input-placeholder { /* WebKit browsers */
    color:    rgba(0,0,0,0.4) !important;
}
:-moz-placeholder { /* Mozilla Firefox 4 to 18 */
    color:    rgba(0,0,0,0.4) !important;
}
::-moz-placeholder { /* Mozilla Firefox 19+ */
    color:    rgba(0,0,0,0.4) !important;
}
:-ms-input-placeholder { /* Internet Explorer 10+ */
    color:    rgba(0,0,0,0.4) !important;
}

.betbuttonred,
.betbuttonblue {
position: relative !important;
    top: -3px !important;
  }

.betbuttonred:active,
.betbuttonblue:active {
  position: relative !important;
  top: 3px !important;
}

.betcard.left {
	  border-color: #d63737 !important;
  	box-shadow: 0px 3px 0px #a02121, 0px 3px 5px rgba(0,0,0,.7) !important; 
}


.betbuttonred {
 	background: #d63737 url(http://saltybet.com/images/bet1.png) no-repeat center center !important;
  border: 2px solid #ca2929 !important;
    box-shadow: 0px 9px 0px #a02121, 0px 9px 5px rgba(0,0,0,.7) !important;
  }

.betbuttonred:active {
box-shadow: 0px 3px 0px #a02121, 0px 3px 5px rgba(0,0,0,.7) !important;
}

.betcard.right {
	  border-color: #3787d6 !important;
  	box-shadow: 0px 3px 0px #2160a0, 0px 3px 5px rgba(0,0,0,.7) !important;  
}


.betbuttonblue {
 	background: #3787d6 url(http://saltybet.com/images/bet1.png) no-repeat center center !important;
  	border: 2px solid #297aca !important;
    box-shadow: 0px 9px 0px #2160a0, 0px 9px 5px rgba(0,0,0,.7) !important;
  }

.betbuttonblue:active {
box-shadow: 0px 3px 0px #2160a0, 0px 3px 5px rgba(0,0,0,.7) !important;
}

#bottomcontent {
 bottom: 20px !important; 
}

#wagerwrapper ul {
 padding: 0 !important; 
}

#wagerwrapper ul li {
 display: inline-block !important; 
  margin-right: 1em !important;
}

#wagerwrapper ul li:last-child {
 margin-right: 0 !important; 
}

#status {
	padding: 10px 0 !important;	
}

#fightcard {
  border: 0 !important;
  padding-left: 25px !important;
  padding-right: 25px !important;
}

#footer {
 bottom: -130px !important; 
}

#footer,
#status {
  background: #0d0d0d !important;
  border-top: 0 !important;
}

#footer {
  margin-top: 20px !important;
}

/* OPTIONS */

#p1name,
#p2name {
   font-size: /*[[player-name-size]]*/ !important; 
   font-weight: bold !important;
   line-height: 1.1 !important;
  }

#/*[[wager-style]]*/ager {
 	padding: 23px 5px !important;
 	background: #333 !important;
 	font-weight: bold !important;
 	border: 2px solid #ffad01 !important;
 	text-align: center !important;
 	color: #ddd !important;
  }

#w/*[[wager-style]]*/ger {
 	padding: 5px !important;
 	background: #ffad01 !important;
 	font-size: 3em !important;
 	font-weight: bold !important;
 	border: 2px solid #ffad01 !important;
 	text-align: center !important;
 	color: #111 !important;
  }

@media screen and (max-width: 1200px){
  #tournamentname {
display: none !important;
}
}

}
