css:
@-moz-document url-prefix("https://www.google.com"), url-prefix("http://www.google.com") {
#gbx3 {
border-color: #000;
background: url(http://i1314.photobucket.com/albums/t577/DistortedOutsider/Backgrounds/CarbonFibre.png);
opacity: 1;
filter: alpha(opacity=100);
}

body {
background: url(http://media.tumblr.com/16f6ef9cd998310ccc998c0cf3db7c58/tumblr_inline_mqk81x7xmM1rlx4pg.png);
}

#hplogo {
background: url(http://media.tumblr.com/255af24f9607c343287b70bcd864552f/tumblr_inline_mqk8j6I5rl1rlx4pg.png) no-repeat !important;
background-size: 275px 95px;
height: 95px;
width: 275px;
}

#hplogo div {
display: none;
}

#gbvg {
background: -webkit-linear-gradient(left, #f00 20%,#ff7300 40%, #ff0 60%, #0f0 80%, #00f 100%);
background: -moz-linear-gradient(left, #f00 20%,#ff7300 40%, #ff0 60%, #0f0 80%, #00f 100%);
}

#fll {
background: -webkit-linear-gradient(left, #00f 20%,#0f0 40%, #ff0 60%, #ff7300 80%, #f00 100%);
background: -moz-linear-gradient(left, #f00 20%,#ff7300 40%, #ff0 60%, #0f0 80%, #00f 100%);
}

#gbi4t, #gbi4t a, #fll a {
color: #000 !important;
}

.gbqfba {
background-color: #f0f;
background-image: none;
color: #000;
}

#gbqfsa, #gbqfsb {
color: #000;
}
}
