css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("playneverwinter.com") {
html, html *{
	background-color:transparent !important;
	background-image:none !important;
	border-color:#CCCCCC !important;
	border-width:1px !important;
	border-collapse:collapse !important;
	color:#999999 !important;
	font-size:10pt !important;
	font-family:Geneva, Arial, Helvetica, sans-serif !important;
	text-decoration:none !important;
	text-shadow: none !important;
	box-shadow: none !important;
}

a{
	/*text-decoration:underline !important;*/
	color:#9999DD !important;
}

h1, h2, h3, h4, h5, h6, h7, h8{
	font-size:10pt !important;
	font-weight:bold !important;
}

img, input[type=image]{
	opacity: 0.1 !important;
}

img:hover, input[type=image]:hover{
	opacity: 0.4 !important;
}
object, embed, param{
	display:none !important;	
	opacity: 0.2 !important;
}

.notification {
    background: #FFFFDD !important;
}

.modal-window {
    background: #EEFFFF !important;
}

.input-field.button input[type="submit"], .input-field.button input[type="button"], .input-field.button button, .input-field.button a {
  color: #444422 !important;
}

.task-list-entry.higherlevel, task-list-entry unmet {
  background: none repeat scroll 0 0 rgba(255, 0, 0, 0.02) !important;
  border: 1px solid #FF9999  !important;
}

.input-field.button input[type="submit"], .input-field.button input[type="button"], .input-field.button button, .input-field.button a {
  color: #444422 !important;
}

.task-slot-finished {
  color: #FF00FF !important;
}

.header-newrelease {
  display:none !important;
}

#content_box {
  margin-top: 150px !important;
}
/*
.epic {
  background-color:#EEFFFF !important;
}

.epic:hover {
  border: 1px solid #55FFFF  !important;
}
*/

.tooltip {
  background-color:#FDFDFD !important;
}

.input-bg-mid {
  height: 30px !important;
  background-color:#F0F0F0 !important;
  border-radius: 10px !important;
}

button[data-url-silent="/professions/finish-now/0"],
button[data-url-silent="/professions/finish-now/1"],
button[data-url-silent="/professions/finish-now/2"],
button[data-url-silent="/professions/finish-now/3"],
button[data-url-silent="/professions/finish-now/4"],
button[data-url-silent="/professions/finish-now/5"],
button[data-url-silent="/professions/finish-now/6"],
button[data-url-silent="/professions/finish-now/7"],
button[data-url-silent="/professions/finish-now/8"] {
  height: 30px !important;
  background-color:#FFEEEE !important;
  border-radius: 10px !important;
}
button[data-url-silent="/professions/collect-reward/0"],
button[data-url-silent="/professions/collect-reward/1"],
button[data-url-silent="/professions/collect-reward/2"],
button[data-url-silent="/professions/collect-reward/3"],
button[data-url-silent="/professions/collect-reward/4"],
button[data-url-silent="/professions/collect-reward/5"],
button[data-url-silent="/professions/collect-reward/6"],
button[data-url-silent="/professions/collect-reward/7"],
button[data-url-silent="/professions/collect-reward/8"] {
  height: 30px !important;
  background-color:#E0FFE0 !important;
  border-radius: 10px !important;
}
}
