@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ngb.to") {
body
{
    background-color: #1A1A1A !important;  
}
.body_wrapper
{
    width: 95% !important;
    margin: 0px auto !important;
    background-color: #1a1a1a !important;   
}

 .forumbit_nopost .forumrow, .forumbit_post .forumhead, .forumbits .forumhead
{
    background-color: #232323 !important;
}

.pagination span a {
    border:1px solid #1c1c1c !important;
}
.forumhead { 
    background-color: #111111 !important; 
    background: #000000 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAGQAAAASCAIAAAAi++zfAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAIGNIUk0AAIcbAACL/wAA/pIAAH7TAACA9wAA6BUAADoHAAAeRSLhXfcAAAA7SURBVHja7NBBDQAgDAQwSFAy/xpvWNiH8GkldFfVYuYksSBLlixZsmRZkPUgq7stAADw1QUAAP//AwA64xtZoHbABQAAAABJRU5ErkJggg==) repeat-x!important;
    height: 18px;
}
.threadlisthead
{
    background: #000000 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAGQAAAASCAIAAAAi++zfAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAIGNIUk0AAIcbAACL/wAA/pIAAH7TAACA9wAA6BUAADoHAAAeRSLhXfcAAAA7SURBVHja7NBBDQAgDAQwSFAy/xpvWNiH8GkldFfVYuYksSBLlixZsmRZkPUgq7stAADw1QUAAP//AwA64xtZoHbABQAAAABJRU5ErkJggg==) repeat-x!important;
    height: 18px;
}
.footer, .blockbody, .forum_info_subblock, .info_subblock, .announcerow .announcement, .announcerow .announceinfo, .announcements .announcerow
{
  background-color: #161616 !important;
}

.threadbit .sticky .threadstats {
    background-color: #161616 !important; 
    border-left:1px solid #1c1c1c !important;
    border-right:1px solid #1c1c1c !important;
    border-bottom:1px solid #1c1c1c !important;
}
.threadbit .alt, .threadbit .threadstats {
    background-color: #232323 !important;
    border-left:0px solid #1c1c1c !important;
    border-right:0px solid #1c1c1c !important;
    border-bottom:0px solid #1c1c1c !important;
    border-top: 0px solid #1c1c1c !important;
}

.threadbit .nonsticky, .threadbit .deleted, .threadbit .discussionrow, .threadbit .ignored {
    border-left:1px solid #1c1c1c !important;
    border-right:1px solid #1c1c1c !important;
    border-bottom:1px solid #1c1c1c !important;
    color: #c0c0c0 !important;
    background-color: #232323 !important; 
}

.announcements {
    border: 1px solid #1c1c1c !important;
}

.forum_info, .blockbody, .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
    border-bottom:1px solid #1c1c1c !important;
    border-left:1px solid #1c1c1c !important;
    border-right:1px solid #1c1c1c !important;
}

.postbitlegacy .postbody, .eventbit .eventdetails .eventbody {
    border-left:1px solid #1c1c1c !important;
}

.threadbit .alt {
    border-left:1px solid #1c1c1c !important;
    border-right:1px solid #1c1c1c !important;
}

.threadbit .sticky{
    border:1px solid #1c1c1c !important;
}

.postbit, .postbitlegacy, .eventbit, .postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead, .postbitlegacy .fish_thx_list, .blockquote.restore hr, #post_preview, .showgroups_table td, .showgroups_table th, .profile_content .userprof_content, .userprof_headers_border, .thread_info h4, .forum_info, .wgo_block .blockbody,  .postlistfoot, .forumfoot,  .toolsmenu, .forumhead, .threadlisthead, .blockhead {
    border:1px solid #1c1c1c !important;
}

.signature, .postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols , #activitylist .activitybit, #usercp_nav .blocksubhead , .forumhead + ol > li:first-child, .threadlist .threadlisthead + ol, .blockrow:first-child, .searchbits > li:first-child, ol#threadlist > li:first-child, #usercp_content ol#threads > li:first-child, .announcements .announcerow, .formcontrols, .postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols {
    border-top:4px solid #1c1c1c !important;
}

.postbit .posttitle, .postbitlegacy .title, #usercp_nav .blocksubhead, #post_preview .blocksubhead, #pmfolderlist .blocksubhead {
    border-bottom:1px solid #1c1c1c !important;
}

#activitylist {
    border-color: -moz-use-text-color #1c1c1c !important;
    border-right: 1px solid #1c1c1c !important;
}

.footer {
    border:1px solid #1c1c1c !important;
}

.postbit .postfoot .textcontrols, .postbitlegacy .postfoot .textcontrols, .eventbit .eventfoot .eventcontrols {
    background: #000000 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAGQAAAASCAIAAAAi++zfAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAIGNIUk0AAIcbAACL/wAA/pIAAH7TAACA9wAA6BUAADoHAAAeRSLhXfcAAAA7SURBVHja7NBBDQAgDAQwSFAy/xpvWNiH8GkldFfVYuYksSBLlixZsmRZkPUgq7stAADw1QUAAP//AwA64xtZoHbABQAAAABJRU5ErkJggg==) repeat-x!important;
    height: 18px;
}

.postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead
{
    background: #000000 url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAGQAAAASCAIAAAAi++zfAAAACXBIWXMAAAsTAAALEwEAmpwYAAAAIGNIUk0AAIcbAACL/wAA/pIAAH7TAACA9wAA6BUAADoHAAAeRSLhXfcAAAA7SURBVHja7NBBDQAgDAQwSFAy/xpvWNiH8GkldFfVYuYksSBLlixZsmRZkPUgq7stAADw1QUAAP//AwA64xtZoHbABQAAAABJRU5ErkJggg==) repeat-x!important;
    height: 18px;
}
}
