css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gran-turismo.wikia.com") {

body.mediawiki {
background: url(http://imageshack.com/a/img198/4552/hx11.png) fixed repeat center top!important;
}

/*Transparent BG*/

.WikiaPage { background: url(http://images2.wikia.nocookie.net/joeplayground/images/3/38/80opacityblack.png) repeat; }

/* Special pages and edit pages */

.AdminDashboardDrawer, .AdminDashboardGeneralHeader, .AdminDashboardHeader { background: black !important; }

.oasis-dark-theme #EditPageHeader { background: black !important; }

#EditPageEditor #myedit > textarea { background-color: black !important; }

#EditPageEditor #diff {
    background-color: #202020;
    border: 1px solid #404040;
}

.WikiaPage .WikiaPageBackground { background: transparent !important; }

.WikiaArticle.AdminDashboardChromedArticle { background: black !important; }

.AdminDashboard .admin-dashboard-content .control-section .controls { background: black !important; }

.AdminDashboard .admin-dashboard-content .control-section header { background: black !important; }

.AdminDashboard .admin-dashboard-content .control-section .controls .control .representation { background: black !important; }

h4.mw-specialpagesgroup {
    background-color: #650000;
    color: white;
}

.TablePager, .TablePager td, .TablePager th {
   background: transparent;
   color: #CCC;
}

#mw-allmessagestable .allmessages-customised td.am_default,
#mw-allmessagestable .allmessages-customised td.am_default:hover
{
   background-color: #672F2F !important;
}

#mw-allmessagestable td.am_actual, #mw-allmessagestable td.am_actual:hover {
   background-color: #214066 !important;
}

.UserProfileMasthead .masthead-info hgroup {
   background-image: -moz-linear-gradient(top, #1A1A1A 0%, black 100%);
   background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, color-stop(0%, #1A1A1A), color-stop(100%, black));
   background-image: -o-linear-gradient(top, #1A1A1A 0%, black 100%);
   background-image: -ms-linear-gradient(top, #1A1A1A 0%, black 100%);
}

.UserProfileMasthead .masthead-info {
   background: #111;
}

.UserProfileMasthead .masthead-info-lower::before {
   border-color: #090909 #090909 transparent transparent;
}

.Wall ul.comments > li.message > .speech-bubble-message {
   background: #090909;
}

.Wall ul.comments > li.message > .speech-bubble-message::after {
   border-color: #090909 #090909 transparent transparent;
}

.Wall .SpeechBubble.new-message .speech-bubble-message {
   background: #090909;
}

.Wall .SpeechBubble.new-message .speech-bubble-message::after {
   border-color: #090909 #090909 transparent transparent;
}

.Wall .new-message textarea, .Wall .new-message .editarea textarea {
   background: #111;
}

/*********************************/
/* main page jquery tabs/sliders */
/*********************************/
.ui-tabs { position: relative; padding: .2em; zoom: 1; }
.ui-tabs .ui-tabs-nav { margin: 0; padding: .2em .2em 0; }
.ui-tabs .ui-tabs-nav li { list-style: none; float: left; position: relative; top: 1px; margin: 0 .2em 1px 0; border-bottom: 0 !important; padding: 0; white-space: nowrap; }
.ui-tabs .ui-tabs-nav li a { float: left; padding: .5em 1em; text-decoration: none; }
.ui-tabs .ui-tabs-nav li.ui-tabs-selected { margin-bottom: 0; padding-bottom: 1px; }
.ui-tabs .ui-tabs-nav li.ui-tabs-selected a, .ui-tabs .ui-tabs-nav li.ui-state-disabled a, .ui-tabs .ui-tabs-nav li.ui-state-processing a { cursor: text; }
.ui-tabs .ui-tabs-nav li a, .ui-tabs.ui-tabs-collapsible .ui-tabs-nav li.ui-tabs-selected a { cursor: pointer; } 
.ui-tabs .ui-tabs-panel { display: block; border-width: 0; padding: 0; background: none; }
.ui-tabs .ui-tabs-hide { display: none !important; }
.ui-tabs-hide { display: none !important; }

/**********/
/* portal */
/**********/
.portal_container { }
.portal_section ul  { list-style: disc; }
.portal_section { border: 1px solid black; margin: 0 0 10px 0; background: transparent; background-image: -moz-linear-gradient(top, black 0, #000930 100%); -moz-border-radius: 1em; -moz-box-shadow: 0 0 5px 5px #101010; background-image: -webkit-gradient(linear, 0% 0%, 0% 100%, color-stop(0, black), color-stop(100%, #000930)); -webkit-border-radius: 1em; -webkit-box-shadow: 0 0 5px 5px #101010; border-radius: 1em; box-shadow: 0 0 5px 5px #101010; }
.portal_header { padding: 0 1em; position: relative; }
.portal_header_text { display: table; width: 100%; padding: 0.6em 0 0 0; border-bottom: 1px dotted #DDD; text-align: center; font-family: IMFELLDWPicaPRORegular; font-size: 2em; }
.portal_headerlinks { font-size: 0.8em; position: absolute; right: 1em; bottom: -2em; }
.portal_header, .portal_header a:link, .portal_header a:visited, .portal_header a:hover, .portal_header a:active { color: #DDD; }
.portal_body { padding: 0.8em; }
.portal_subheader { padding: 0 1em; position: relative; font-size: 0.9em; }
.portal_subheader, .portal_subheader a:link, .portal_subheader a:visited, .portal_subheader a:hover, .portal_subheader a:active { color: #DDD; }
.portal_subheader_text { display: table; width: 100%; padding: 0.4em 0 0 0; border-bottom: 1px dotted #DDD; text-align: left; font-family: IMFELLDWPicaPRORegular; font-size: 1.8em; color: #DDD; }

/******************/
/* portal: slider */
/******************/
#portal_slider { margin: 0px; padding: 0px; position: relative; height: 400px; }
#portal_slider #portal_next, #portal_slider #portal_prev { text-align: center; cursor: pointer; border: 0px solid #FEC356; height: 30px !important; width: 20px; font-family: arial, san-serif; font-size: 36px; line-height: 22px; font-weight: bold; position: absolute; bottom: 8px; background: transparent; -moz-box-shadow: inset 1px 1px 2px rgba(255,255,255, 0.8); -webkit-box-shadow: inset 1px 1px 2px rgba(255,255,255, 0.8); box-shadow: inset 1px 1px 2px rgba(255,255,255, 0.8); background-image: -moz-linear-gradient(bottom, rgb(75,75,75) 0, rgb(165,165,165) 100%); background-image: -webkit-gradient(linear, 0% 100%, 0% 0%, color-stop(0, rgb(75,75,75)), color-stop(100%, rgb(165,165,165))); text-shadow: 1px 1px rgba(255, 255, 255, 0.3), -1px 0 rgba(0,0,0,0.3); color: rgb(115,115,115); -moz-user-select: none; -webkit-user-select: none; user-select: none; }
#portal_slider #portal_next:hover, #portal_slider #portal_prev:hover { background-image: -moz-linear-gradient(bottom, rgb(90,90,90) 0, rgb(185,185,185) 100%); background-image: -webkit-gradient(linear, 0% 100%, 0% 0%, color-stop(0, rgb(90,90,90)), color-stop(100%, rgb(185,185,185))); color: rgb(140,140,140); }
#portal_slider #portal_next { right: 10px; -moz-border-radius-topright: 8px; -moz-border-radius-bottomright: 8px; -webkit-border-top-right-radius: 8px; -webkit-border-bottom-right-radius: 8px; border-top-right-radius: 8px; border-bottom-right-radius: 8px; }
#portal_slider #portal_prev { right: 31px; -moz-border-radius-topleft: 8px; -moz-border-radius-bottomleft: 8px; -webkit-border-top-left-radius: 8px; -webkit-border-bottom-left-radius: 8px; border-top-left-radius: 8px; border-bottom-left-radius: 8px; }
#portal_slider > .portal_body { padding: 0; }


/*******************/
/* portal: content */
/*******************/
#portal_content { font-size: 0.9em; line-height: 1.7em; font-family: Verdana, Arial, Helvetica, sans-serif; }
#portal_content .portal_body > table { height: 350px; }
#portal_content ul { list-style: disc outside; }
#portal_content ul ul { margin-left: 1em; }

/*******************************/
/* NewPages: Un-patrolled pages*/
/*******************************/

.not-patrolled {background: #460000 !important;}

/*******************************/
/* History: Diff view*/
/*******************************/
table.diff, td.diff-otitle, td.diff-ntitle {
    background-color: transparent !important;
}

/*******************************/
/* Red "Contribute" and "Share" buttons*/
/*******************************/

.WikiHeader .buttons .contribute {
    padding-top: 1px;
    vertical-align: middle;
    background: #8b000 !important;
}

.WikiHeader .buttons .share-button {
    padding: 1px 18px 0px 9px;
    position: relative;
    background: #8b0000 !important;
}


}
