css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {
body { 
    background-image:url("http://i.imgur.com/hu3clc8.png")!important;
  	background-position: top left !important;
	background-repeat: no-repeat !important;
	background-attachment: fixed !important; 
	background-color: #B08DA0 !important;
}

#left_column, #right_column {background-color: transparent !important;}

div.l-content.l-content.l-content {
   background: rgba(27,0,0,0.1)
 }

#home_button {

    padding-right: 5px !important;
}

#home_button a {

    height: 0 !important;
    width: 0 !important;
    top: 0px !important;
    padding-left: 38px !important;
    padding-top: 40px !important;
    background: url('http://i.imgur.com/OWNKyVl.png') !important;
}

#home_button .tab_notice {
    left: 40px !important;
    right: auto !important;
}
div.l-header .selection_nipple {
width: 0;
height: 0;
border-left: 12px solid transparent;
border-right: 12px solid transparent;
border-bottom: 12px solid #fff;
opacity:0.8;
font-size: 0;
position: relative;
top: 1px;
vertical-align: top;
margin: 0 auto;
display: none;
}
}
