css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("youtube.com") {

#yt-masthead-container {
    background: none repeat scroll 0 0 #3B5998 !important;
    border-bottom: 1px solid #133783 !important;
    padding-bottom: 8px !important;
    padding-top: 7px !important;
}

#yt-masthead-user-displayname {
    color: #D8DFEA !important;
    font-weight: bold !important;
    margin-right: 5px !important;
}

#yt-masthead-user:hover {
    background-color: #4B67A1 !important;
    color: #FFFFFF !important;
    outline: medium none !important;
    z-index: 1 !important;
}

#guide-container .guide-item.guide-item-selected, #guide-container .guide-item.guide-item-selected:hover {
    background: none repeat scroll 0 0 #D8DFEA !important;
    color: #333333 !important;
}

#guide-container .guide-item.guide-item-selected {
    font-weight: bold !important;
    text-shadow: none !important;
}

.context-back-link:hover, #guide-container .guide-item:hover {
    background: none repeat scroll 0 0 #EFF2F7 !important;
    color: #000000 !important;
}

.guide-section-separator {
    border-bottom: none !important;
    margin: none !important;
}

.yt-house-channel-label, .yt-featured-channel-label {
    background-color: #F2F2F2 !important;
    color: #666666 !important;
    border-bottom: medium none !important;
    border-top: 1px solid #E2E2E2 !important;
    padding: 4px 6px 5px !important;
    cursor: default !important;
    font-size: 11px !important;
    font-weight: bold !important;
    text-transform: uppercase !important;
}

.channel-header-profile-image {
    background-color: #FFFFFF !important;
    box-shadow: 0 1px 1px rgba(0, 0, 0, 0.4) !important;
    border: 4px solid #FFFFFF !important;
    border-radius: 2px 2px 2px 2px !important;
    margin-top: 60px !important;
    height: 160px !important;
    width: 160px !important;
}

.qualified-channel-title.ellipsized .qualified-channel-title-wrapper {
    color: #1C2A47 !important;
    display: inline-block !important;
    font-weight: bold !important;
    font-size: 20px !important;
    max-width: 100% !important;
    margin-left: 180px !important;
}

.channel-header-autogenerated-label {
    bottom: 20px !important;
    color: #DFDFDF !important;
    left: 210px !important;
    position: absolute !important;
    margin-top: 200px !important;
}

}
