css:
@-moz-document domain(search.seznam.cz){
.content p span span { display: inline !important; position: absolute !important; width: 80px !important; text-align: right !important; right: 0.2em !important; color: #999 !important; border-bottom-color: #999 !important;}
.content p.item { border-bottom: 1px dotted #ccc !important;}
.content p.item.active { background-color: #ddd !important;}
.content p.item.active span span { color: #fff !important;}
.content p.item.active span, .content p.item.active span span { color: #000 !important;}
.content p.item.active strong { font-weight: normal !important; color: #000 !important;}
}
