css:
@-moz-document url-prefix("http://reader.youdao.com/") {

* {font-family: "Microsoft YaHei" ! important;}  /*雅黑*/

#top{display:none  !important;}    /*顶栏去除*/
#main {top:0px !important;}    
#navigationBottom {bottom:0px !important;}


#favorite {padding-bottom:40px !important;}
#share {padding-bottom:40px !important;}
#subTree {padding-bottom:40px !important;}

#addSubscribeBtn {position:fixed !important;right:-75px !important;z-index:1}
#addSubscribeBtn:hover {right:0px !important;z-index:1;-o-transition: .3s ease-in;
-moz-transition: .3s ease-in;
-webkit-transition: .3s ease-in;}     /*添加订阅*/



#current-read ._gosource_{color:#C60 !important;}
#current-read .cast-container {background-color:rgb(239, 237, 238) !important;border-style:dashed !important;border-color:gray !important;
border-width:2px !important;}
.cast-container {background-color:white !important;border-style:dashed !important;border-color:gray !important;
border-width:2px 0 0 0 !important;}
.cast-function-bar {background-color:white !important;}

.cast-title {max-width:100% !important;text-align:center !important;}   /*内容居中显示*/
.cast-content {max-width:100% !important;margin:0 7% 0 7% !important}

.cast {border-bottom-color:whiteSmoke !important;}
.cast-clip-container {background-color:white !important;}
.cast-clip {background-color:white !important;}
#contentPageBar {background-color:white !important;}
#toggleNavigation {background-color:white !important;}
#contentFunctionBar {background-color:white !important;}
.readed .cast-clip {background-color:white !important;}
.readed .cast-wrapper {border-color:white !important;}

#contentTitle {padding:0.5em !important;border-style:solid !important;border-width:0 0 1px 0 !important;
border-bottom-color:#ebebeb !important;font-size:1.5em !important;}
#contentFunctionBar {font-size:1.2em;padding:10px !important;}
.btnL {background:none !important;}
.btnR {background:none !important;}
#navigation {background-color:#eee !important;}       
#toggleNavigation.half-toggle {top:0px !important;}     /*分界栏*/        
#contentTitle {height:25px;right:0px !important;}
}
