css:
#twitch_chat {
  background-color: black;
}


#twitch_chat #right_col .content ul#chat_line_list { margin: 0; padding: 0; }
#twitch_chat li.line {
  border-bottom: 0px solid #202020;
  padding: 0;
  margin: 0 0 3px;
  font-family: 'Tahoma', sans-serif;
  color: #687851;
  word-wrap: normal;
}
#twitch_chat li.line * {
  line-height: 18px;
}
#twitch_chat li.line > p {
  font-size: 11pt !important;
  color: #687851;
  margin: 0;
  font-weight: normal;
}

#twitch_chat li.line span.tag { margin-top: -3px; height: 18px; }
#twitch_chat li.line span.tag.subscriber { background-color: blue; }
#twitch_chat li.line span.tag.mod { background-color: #008000; }
#twitch_chat li.line span.tag.turbo { background-color: #6441a5; }
#twitch_chat li.line span.tag.broadcaster { background-color: red; }

#twitch_chat a[class="nick"]:before { color: #687851; content: '<'; font-weight: normal; }
#twitch_chat a[class="nick"] { text-transform: lowercase; }
#twitch_chat a[class="nick"]:after { color: #687851; content: '>'; font-weight: normal; }
#twitch_chat a[class="nick"] { height: 15px; }
#twitch_chat a[style="color:black"], #twitch_chat a[style="color:#000000"] { color: #bbb !important; }

#twitch_chat li.line span.colon { display: none; }
#twitch_chat a[class="nick"] { margin-right: 6px; }
