css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document regexp('http://.*./chatbox.*') {

* {
    margin: 0;
    padding: 0;
}
html, body {
    height: 100%;
    width: 100%;
}
body {
    font-family: "Segoe UI",Tahoma,Helvetica,Sans-Serif;
    font-size: 11pt;
    letter-spacing: 0.02em;
    line-height: 14pt;
}
#bv_main * {
    box-sizing: border-box !important;
    -moz-box-sizing: border-box !important;
}
#bv_main {
    background: #FFFFFF;
    border: 1px solid #DDDDDD;       
    bottom: -310px;
    float: left;
    left: -330px;
    padding: 10px !important;
    position: fixed;
    z-index: 999;
    transition: bottom .5s, left .5s, transform .5s;
	-webkit-transition: bottom .5s, left .5s, -webkit-transform .5s;
}
#bv_main:hover{
    bottom: 0;
    left: 0;
}
#bv_main > div {
    border: 1px solid #0084FF;
    padding: 5px !important;
    text-align: center;
    width: 355px;
}
#tab_emo {
    background: url(http://i.imgur.com/s0Pcl2k.png) no-repeat scroll right top transparent;
    height: 30px;
    width: 100%;
}
#tab_emo li {
    display: inline;
    list-style: none outside none;
}
#tab_emo a {
    color: #0084FF !important;
    display: inline-block;
    height: 30px;
    line-height: 30px;
    padding: 0 10px !important;
    text-decoration: none;
}
#tab_emo a.active {
    background: #0084FF;
    color: #FFFFFF !important;
}
#bv_emo, #bv_about {
    display: none;
}
.bv_content {
    border-bottom: 1px solid #DDDDDD;
    height: 237px;
    margin-bottom: 10px !important;
    padding: 5px !important;
}
.bv_content a {
    background: none no-repeat scroll center center transparent;
    display: inline-block;
    height: 75px;
    width: 75px;
}
.bv_pag {
    display: inline-block;
    font-size: 80%;
}
.bv_pag a {
    border: 1px solid #AAAAEE;
    color: #1155BB;
    text-decoration: none;
}
.bv_pag a, .bv_pag span {
    display: block;
    float: left;
    margin-bottom: 5px !important;
    margin-right: 5px !important;
    padding: 0.3em 0.5em !important;
}
.bv_emoticon {
    text-indent: -9999px;
}
.bv_pag .current {
    background: #2266BB;
    border: 1px solid #AAAAEE;
    color: #FFFFFF;
}
.bv_pag .current.prev, .bv_pag .current.next {
    background: #FFFFFF;
    border-color: #999999;
    color: #999999;
}

}
