css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("app.ryzom.com") {

.post-header {
    background-color: #1E1E1E !important;
    height: 1.2em;
    margin: 0;
    padding: 0.3em;
}

div.post-container {
    background-color: #FFFFFF !important; 
}

.post-container {
    background-color: #FFFFFF !important; 
}

.post-container, .single-post-container {
    clear: both;
    color: #1E1E1E !important;
    font-size: 100%;
    font-family: sans-serif;
    padding-bottom: 1em;
}

.post-quote {
    background-color: #DDDDDD !important;
    border: 1px solid #999999;
    margin: 0.5em;
    padding: 0;
}

.post-quote blockquote {
    background-color: #FFFFFF !important;
    margin: 0;
    padding: 0.2em;
}

body {
    background-image:url("http://vaultmedia.ign.com/rpgvault/image/article/644/644551/ryzom082404a_1124851906.jpg") !important;
    background-size: 100%;
    background-repeat: repeat !important;
    font-family: serif;
    font-size: 12px;
}

.post-author{
    background-color: #DDDDDD !important;
}

.post-body span {
    text-shadow: -1px 0 1px gray, 0 1px 1px gray, 1px 0 1px gray, 0 -1px 1px gray;
    font-size: 110%;
}

}
