css:
@-moz-document domain('htmlbook.ru') {
	/* htmlbook.ru color scheme redesign by ArtDesire, Top-5ive.Net */
	header {
		background-color: white;
	}
	#topmenu .show,.tile {
		background:#eee;
	}
	#topmenu li {
		background: white;
	}
	aside {
		background:#f7f7f7;
	}
	aside a {
		color:#222;
	}
	aside a:visited {
		color:#555;
	}
	aside a:hover {
		color:#777;
	}
	.logo {
		margin: 0;
		padding: 0 16px 0 10px;
		background-color: #666;
	}
	.hosting:before {
		content: "htmlbook.ru scheme by ArtDesire, Top-5ive.Net    ";
	}
	.active-trail a.active, .open a.active, #views-practical_block-theme .active, #views-blog_last-block .active {
		background-color: #eee;
	}
	aside .block_title {
		background:#BEC4CF;
		color:#555;
		text-shadow: none;
	}
	.br td, .spec th, .cssver th {
		background-color: #F5F5F5;
		font-weight: normal;
	}
	.sp td.yes, .yes {
		background: #85FF7B;
	}
	.sp td.no, .no {
		background: #FFA3A3;
	}

}
