css:
@-moz-document 
domain("soundcloud.com")

{

body, #content, div.l-footer.sc-text-verylight.sc-border-dark-top, DIV.listenContent__inner
{
background: #111111 !important;
}

a.playbackTitle.sc-truncate.disabled, span, a.soundActions__purchaseLink.sc-truncate.sc-buylink.sc-buylink-responsive.sc-buylink-small.sc-buylink-default, div.trackPreview__pre.sc-type-light, a.trackPreviewItem__titleLink.sc-truncate, a.soundActions__purchaseLink.sc-truncate.sc-buylink.sc-buylink-responsive.sc-buylink-small.sc-buylink-beatport, a.userBadge__userNameLink.sc-link-dark, label
{
color: white !important;
}

div.soundBadge.g-all-transitions-300.sc-media.compact.hiddenActions.playlist, div.soundBadge.g-all-transitions-300.sc-media.compact.hiddenActions, div.soundBadge.g-all-transitions-300.sc-media.compact, div.l-footer.sc-text-verylight, div#sidecart_item_
{
background: #4F4F4F !important;
}

a.soundTitle__username.sc-link-light, a.commentItem__username.sc-link-light, a.userBadge__userNameLink.sc-link-dark, h1.userInfo__title, td.statsBadge__value.sc-border-light-right.sc-text-link
{
color: #FF5500 !important;
}

a.soundTitle__title.sc-link-dark, a.playbackTitle.sc-truncate, span.soundTitle__title, td.statsBadge__value.sc-text-verylight
{
color: #FF8100 !important;
}

span.sc-ministats.sc-ministats-small.sc-ministats-reposts, H3.trackListTitle.sc-type
{
color: #FFC700 !important;
}


button.sc-button.sc-button-small.sc-button-responsive.sc-button-addtoset
{
color:black !important;
}

div.listenContent__inner.l-container, div.miniPlaylistIndicator
{
background: #222222 !important;
}

DIV.soundBadge.sc-media.dragHandle.active
{
background: #333333 !important;
}

p
{
color: #888888 !important;
}

div.sound__soundActions, DIV.soundBadge__additional, span
{
background: transparent !important;
}

button.sc-button.sc-button-small.sc-button-responsive.sc-button-addtoset, button
{
color: #111111 !important;
}

}
