css:
@-moz-document domain("bazqux.com") {
div.infoMsgText{
  	background-color:#3E4957!important;
  	color:#fff!important;
  	border:#3E4957!important;
	}
.selected.post.msgFrame{
  	border:1px solid #ccc!important;
	}
.msgFrame{
    margin:20px 50px!important;
    border:1px #eee solid!important;
    border-radius:5px!important;
    }
.expandable.unselected.compact.post.msgFrame,
.read.expandable.unselected.compact.post.msgFrame{
    margin:0!important;
  	border-radius:0!important;
  	border:0!important;
  	border-top:1px solid #eee!important;
  	border-left:1px #fff solid!important;
    }
.expandable.selected.compact.post.msgFrame,
.read.expandable.selected.compact.post.msgFrame{
    margin:0!important;
  	border:0!important;
  	border-top:1px solid #eee!important;
  	border-radius:0!important;
  	border-left:1px #444 solid!important;
    }
.snapPoint{margin-top:-20px!important;}
.msgBody p{
    margin-top:20px!important;
    }
.unreadCount{
    background-color:inherit!important;
    color:#ccc!important;
  	border:0!important;
	}
cite{
  	color:#696969!important;
	}
blockquote{
  color:#696969!important;
  border-left:3px solid #eee!important;
  padding-left:20px!important;
  margin:20px 0!important;
  display:block!important;
}
.postHeader{
  background-color:#fff!important;
  font-size:14px;
  }
h1,
h2,
h3,
h4,
h5{
  color:#c13082!important;
  font-weight:bold;
  }
.msgBody a{
  color:#c13082!important;
  text-decoration:none;
  }
a.msubject{
  	color:#444!important;
    font-size:26px!important;
  	font-weight:bold!important;
  	line-height:1.2!important;
  	padding-bottom:5px!important;
  	text-align:left!important;
  	}
.mauthor{
  	display:block!important;
  	font-size:14px!important;
  	}
.fromFolder{
  	font-size:14px!important;
  	}
.mtext *{
    font-size:18px!important;
    line-height:1.6!important;
    color:#444;
	font-family:Helvetica,Arial!important;
	}
.msgBody{
	padding:20px 0!important;
    max-width:75%!important;
  	margin:0px auto!important;
  	text-align:justify!important;
    }
.msgBody iframe{
  	width:90%!important;
    height:400px!important;
  	}
.authorPic{
  display:none!important;
	}
.mtags,
.favicon{
  display:none!important;
	}
.mtextPad{
  	margin-top:40px!important;
  	}
.msgFooter{
  	padding:0 0 30px 105px!important;
  	}
.mauthor a {
  	color:inherit!important;
    }
.msgToolBar,
.subToolBar{
    margin-top:27px!important;
  	background-color:#eee!important;
  	height:30px!important;
  	font-size:12px!important;
    }
.subToolBar a,
.msgToolBar a{
  	color:#808080!important;
    }
.subToolBar a:hover,
.msgToolBar a:hover{
  	color:#444!important;
    }
.left,
.right{
  	margin-top:27px!important;
    }
.left{
  	background-color:#3E4957!important;
    }
.left li{
    font-weight:normal!important;
  	padding:2px 10px!important;
  	color:#eee!important;
  	font-size:13px!important;
  	}
.subscriptions li {
  	border-color:#444!important;
  	}
.noTouch .subscriptions li:hover{
  	background-color:#515B68!important;
    }
.subscriptions li.selected, 
.noTouch .subscriptions li.selected, 
.subscriptions li.selected .buttonChar{
  border-left-color:#eee!important;
	}
.subscriptionTitle{
  	font-size:25px!important;
    padding:20px 20px 10px 20px!important;
  	color:#444!important;
  }
.subscriptions,
.subscriptionTitle{
    padding-top:10px!important;
  	}
.searchToolBar {
  	height:25px!important;
    border:1px solid #eee!important;
  	padding:1px!important;
  	margin-top:9px!important;
  	background-color:#fff!important;
  	}
.searchToolBar input{
    width: 400px!important;
    font-size:16px!important;
  	margin:0!important;
  	}
.searchToolBar .button{
  	margin:0!important;
  	padding:5px!important;
  	color:#fff!important;
  	width:50px!important;
	background-color: #c13082!important;
	background-image:none!important;
	}
.top{
  	height:50px!important;
    color:#444!important;
  	background-color:#83004a!important;
	}
.logo{
  	font-size:22px!important;
	}
.logoReader,
.logoBaz,
.logoQux{
  	color:#fff!important;
    }
.logoQux{
    border-right:1px solid #C5C8CD;
    padding-right:7px;
    }
.button, 
.textButton,
.button a:hover{
	background-color: #eee!important;
	background-image: none!important;
	padding:4px 10px!important;
    font-size:14px!important;
  	border:0!important;
  	margin-top:2px!important;
  	margin-left:10px!important;
	}
.msgButtons{
    margin:10px;
  	}
}
