css:
@-moz-document url-prefix("http://www.netvibes.com") {
body {
  background: none repeat scroll 0 0 white !important; }

#columns {
  padding-top: 10px !important; }

#modulesArea {
  margin: 0 0 0 6px !important; }

#header {
  background: none !important; }

#header .overlay {
  background: none !important; }

#topLinks {
  display: none !important; }

#footer {
  display: none !important; }

a.actions-tab-options {
  display: none !important; }

#divTabs {
  margin: 6px 14px 0 !important; }

#divTabs .unread {
  color: black !important; }

#pageTitle,
.inputPageTitle {
  display: none !important; }

#tabSearchForm {
  display: none !important; }

#divTabs ul li {
  background: none !important; }

#divTabs ul li .innerTab {
  background: none !important;
  padding: 0 !important; }

#divTabs ul li.selected {
  background: none !important; }

#divTabs ul li.selected .innerTab {
  background: none !important; }

#divTabsOuter {
  border-bottom: none !important;
  border-top: none !important; }

#divTabs > .nv-tabs-right {
  height: 27px;
  z-index: 99;
  top: -6px;
  right: -14px; }

.module,
.moduleEdit {
  background: none !important; }

.moduleFrame {
  background: none !important; }

.moduleHeaderContainer,
.moduleHeader {
  background: none !important; }

.moduleContent {
  background: none !important; }

#top {
  width: 100%; }

#top:hover {
  top: 0px; }

/* Removes Big green Buttons left top */
#contentAnalyseContainer .left-drawer,
#contentAnalyseContainer .right-drawer {
  background-image: none;
  text-shadow: none; }

#contentAnalyseContainer {
  height: 32px;
  margin-bottom: -3px;
  -moz-box-shadow: 0px 0px 0px 0px #000;
  -webkit-box-shadow: 0px 0px 0px 0px black;
  -o-box-shadow: 0px 0px 0px 0px #000;
  box-shadow: 0px 0px 0px 0px black; }

#contentAnalyseContainer.withAnalyse #analyseContentLink .left-drawer {
  border-left: none; }

#contentAnalyseContainer.withAnalyse #analyseLink .left-drawer {
  border-left: none; }

#contentAnalyseContainer.withAnalyse #addContentLink .right-drawer {
  border-right: none; }

/* Removes the red dot */
#savingStatus {
  display: none !important; }

/* Tabs */
#divTabs {
  padding: 0 !important; }

#divTabsOuter {
  border-width: 0 0 1px !important; }

/* Main div: removes the left padding */
#maintable {
  padding-left: 3px !important; }

/* Columns: removes the top padding */
#columns {
  padding-top: 0 !important; }

/* Widgets: removes extra margins */
/* Regular widgets */
.module {
  margin: 1px !important; }

.moduleHeaderContainer {
  border-top: 0 !important; }

.moduleHeader {
  padding: 2px 0 4px 0 !important; }

.moduleContent {
  padding: 2px 0 0 7px !important; }

ul.nv-feedList {
  padding-left: 0 !important; }

.nv-pager {
  padding: 0 !important; }

/* Unread stuff */
.nv-thumbnailedList .read,
.nv-thumbnailedList .read h3,
.nv-thumbnailedList .read h3 a {
  color: black !important; }

.nv-thumbnailedList .has-thumbnail {
  border-left: 4px solid black; }

.nv-thumbnailedList .read {
  border-left: 4px solid red; }

.nv-thumbnailedList .read img {
  opacity: 1;
  filter: alpha(opacity=100); }

span.unread {
  display: none; }

span.counter {
  display: none !important; }

/* Widgets with thumbnails (when "Show more details" is activated) */
ul.nv-thumbnailedList .item {
  padding: 0 !important; }

/* Gripper: reduces its size */
.gripper {
  margin-top: 0 !important;
  width: 2px !important;
  padding: 4px !important; }

/* Removes the footer */
#footer {
  display: none !important; }

/* Removes the red dot */
a:hover .share {
  display: none !important; }

a.edit {
  height: 12px;
  width: 6px; }

a:hover .edit {
  height: 12px;
  width: 6px; }

.has-thumbnail .description {
  display: none; }

.has-thumbnail h3 {
  margin-left: 47px !important; }

.feedThumbnail img.thumb {
  max-height: 30px !important;
  max-width: 40px !important; }

.feedThumbnail {
  height: 30px !important;
  width: 40px !important; }

}
